    opacity: 0;
    transform: scale(0.8);
  }

  &.visible {
    transform: scale(1);
    opacity: 1;
  }
}

// General
/* scss-combine:global/fonts */
@font-face {
  font-family: "Social Icons";
  src: url("/assets/pixelunionsocialicons-regular.eot");
  src: url("/assets/pixelunionsocialicons-regular.eot?#iefix") format("embedded-opentype"),
       url("/assets/pixelunionsocialicons-regular.woff") format("woff");
  font-weight: normal;
  font-style: normal;
}

/* scss-combine:lib/typography */
$primary-font: stencilFontFamily('regular-font');
$heading-font: stencilFontFamily('heading-font');
$heading_style: stencilString('heading_style');

%heading-text-treatment {
  font-family: $heading-font;
  font-weight: 700;
  color: $heading-color;
  text-transform: $heading_style;
}

%input-text-treatment {
  font-family: $primary-font;
  font-size: rem(12);
  font-weight: 400;
  line-height: 2.17;
  letter-spacing: em(1.5, 12);
  color: $input-color;
}

%nav-text-treatment {
  @include basic-ease;
  font-family: $primary-font;
  font-size: rem(14);
  font-weight: 700;
  letter-spacing: em(1.5, 14);
  color: $nav-color;
  border: none;
  text-transform: uppercase;

  &:hover {
    color: $accent-color;
  }
}

%body-text-treatment {
  font-family: $primary-font;
  font-size: rem(16);
  line-height: 1.62;
  color: $body-color;
}

h1,
h2,
h3,
h4,
h5,
h6,
.section-title,
.page-title {
  @extend %heading-text-treatment;
}

/* scss-combine:global/general */
/* General */

body {
  font-family: $primary-font;
  color: $body-color;
  font-size: rem(16);
  line-height: rem(26);
  background-color: $background-color;
}

.rte {
  ul {
    margin-bottom: $gutter-half;
    list-style: inside disc;
  }

  ol {
    margin-bottom: $gutter-half;
    list-style: inside decimal;
  }

  img {
    max-width: 100%;
    height: auto;
  }
}

/* Links */

a {
  @include basic-ease;
  color: $accent-color;
  text-transform: uppercase;
  font-size: rem(13);
  font-weight: 700;
  letter-spacing: 0.1em;
  line-height: normal;
  border-bottom: 1px solid $accent-color;

  &:hover {
    border-bottom-color: transparent;
  }

  &.reverse-link {
    border-bottom-color: transparent;

    &:hover {
      border-bottom-color: $accent-color;
    }
  }
}

/* Basic elements */

hr {
  background: $border-color;
}

cite {
  color: $nav-color;
}

.icon-sprite {
  display: none;
}

/* Container */

.container {
  max-width: rem(1440);
  margin: 0 auto;

  // make sure container never borders viewport
  @include media($max: 1500px) {
    padding-right: $gutter;
    padding-left: $gutter;
  }
}

// ---------------------------------------------------------------- */
// Hiding and Showing Content
// Snippets borrowed from a11yproject:
// http://a11yproject.com/posts/how-to-hide-content/

%visually-hidden,
.visually-hidden {
  // scss-lint:disable ImportantRule
  position: absolute !important;
  width: 1px !important;
  height: 1px !important;
  padding: 0 !important;
  overflow: hidden;
  clip: rect(1px, 1px, 1px, 1px);
  border: 0 !important;
}

body:hover .visually-hidden a,
body:hover .visually-hidden input,
body:hover .visually-hidden button {
  // scss-lint:disable ImportantRule
  display: none !important;
}

.hidden {
  display: none;
  visibility: hidden;
}

.visible {
  display: block;
}

// ---------------------------------------------------------------- */

/* Scroll Locking */

.scroll-locked {
  overflow: hidden;
}

.body-locked {
  @extend .scroll-locked;
}

.scroll-locked-medium {
  @include breakpoint(m) {
    overflow-y: hidden;
  }
}

.show-for-sr,
.sr-only {
  @extend %visually-hidden;
}

.disabled {
  // scss-lint:disable ImportantRule
  cursor: not-allowed !important;
}

/* scss-combine:global/form */
.form-input {
  @extend %input-text-treatment;
  display: block;
  height: 45px;
  width: 100%;
  padding: rem(9) rem(10);
  background: $input-background-color;
  border-color: $input-border-color;

  &::placeholder {
    color: lighten($nav-color, 10%);
  }

  &:focus,
  &:active {
    border-color: $input-color;
    outline: none;
  }
}

// -------------------------- Form Errors -------------------------- //
// Default error classes, as dictated by validetta.js

.form-inline-message {
  display: block;

  .form-field-invalid & {
    font-size: rem(12);
    font-weight: 700;
    color: $alert-error;
  }
}

// ---------------------------------------------------------------- //
// Select Wrappers - Provides some additional style control for select elements

.form-select-wrapper {
  display: block;
  position: relative;
  height: 45px;
  min-width: 250px;
  cursor: pointer;

  select {
    @extend %input-text-treatment;
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    width: 100%; // needed for Firefox
    height: 100%; // needed for IE
    padding: rem(9) rem(10);
    cursor: pointer;
    appearance: none;
    background-color: $input-background-color;
    border: 1px solid $input-border-color;
    border-radius: 0;
  }

  &:after {
    position: absolute;
    top: 46%;
    right: rem(12);
    height: rem(6);
    width: rem(6);
    border-right: 1px solid currentColor;
    border-bottom: 1px solid currentColor;
    transform: rotate(45deg) translateY(-50%);
    content: "";
  }
}

// ---------------------------------------------------------------- //
// Common Form Classes:

// Wraps each field component
.form-field {}

// Form label - applied to the label itself. Prefer to style off titles.
.form-label {}

// Applied to labels that are used as option titles
.form-label-title {
  display: block;
}

.product-option-title {}
.form-option-title {}

// Applied to labels used as option text (radios/checkboxes)
.form-label-text {}
.product-option-text {}
.form-option-text {}

// inline inputs
.form-field-product-list,
.form-field-checkbox,
.form-field-radio {
  .form-field-control {
    display: inline-block;
  }

  .form-input {
    display: inline-block;
  }
}

.form-field-checkbox {
  .form-label-text {
    font-size: rem(15);
  }
}

// Required form element display 'required' text next title
.required-text {
  color: $accent-color;
}


// ---------------------------------------------------------------- */
// Date Field Components

.form-field-date {
  @extend %clearfix;
  margin-bottom: $gutter;

  .form-field-title {
    display: block;
    margin-bottom: $gutter-half;
  }

  .form-input {
    float: left;
  }

  .form-field-control {
    font-size: 0;
  }

  .form-date-year,
  .form-date-month,
  .form-date-day {
    display: inline-block;
    width: 32%;
    min-width: auto;
  }

  .form-date-month {
    margin: 0 rem(5);
  }
}

.form-input.form-radio {
  width: 0;
  height: 0;
  opacity: 0;

  .form-label-text {
    color: rgba($nav-color, 60);
    font-size: rem(14);
    line-height: 1.86;
  }

  ~ .form-label-text::before {
    display: inline-block;
    width: rem(15);
    height: rem(15);
    margin-right: $gutter/2;
    font-size: rem(4);
    line-height: rem(15);
    vertical-align: middle;
    border: 1px solid $border-color;
    content: "";
    cursor: pointer;
  }

  &:hover ~ .form-label-text::before {
    border-color: $accent-color;
  }

  &:checked ~ .form-label-text::before {
    background: svg-url('<svg xmlns="http://www.w3.org/2000/svg" width="10" height="8" viewBox="0 0 10 8"><path fill="#{$accent-color}" fill-rule="evenodd" d="M3.61538462 5.35L1.53846154 3.325.5 4.3375 3.61538462 7.375 9.5 1.6375 8.4615385.625"/></svg>') no-repeat center;
    border-color: $accent-color;
  }
}

.form-input.form-checkbox {
  @extend %visually-hidden;

  .form-label-text {
    color: rgba($nav-color, 60);
    font-size: rem(14);
    line-height: 1.86;
  }

  ~ .form-label-text::before {
    position: relative;
    top: rem(3);
    display: inline-block;
    width: rem(15);
    height: rem(15);
    margin-right: $gutter/2;
    font-size: rem(4);
    line-height: rem(15);
    border: 1px solid $border-color;
    content: "";
    cursor: pointer;
  }

  &:hover ~ .form-label-text::before {
    border-color: $accent-color;
  }

  &:checked ~ .form-label-text::before {
    background-color: $accent-color;
    border-color: $accent-color;
    box-shadow: inset 0 0 0 2px rgba($background-color, 1);
  }
}

.swatch-pattern {
  background-size: cover;
}

.form-field-textarea textarea {
  height: auto;
}

.form-file-wrapper {
  .form-file {
    height: auto;
    line-height: 0;
  }
}

.form-file-size {
  font-size: rem(12);
}

/* scss-combine:global/table */
/* Tables */

table {
  color: $body-color;
  border-color: $border-color;
}

th {
  @extend %heading-text-treatment;
  color: $input-color !important;
  background: $input-background-color;
}

/* scss-combine:components/reviews */
// Submission Modal
.review-modal-container {
  display: none;
}

// Rating Stars (clickable)
.input-stars {
  position: relative;
  display: inline-block;

  &.form-field-error {
    padding-bottom: rem(20);
  }

  .star {
    float: right;
    width: rem(20);
    height: rem(20);
    cursor: pointer;
    fill: rgba($body-color, 0.3);

    &:hover,
    &:hover ~ .star svg {
      fill: $accent-color;
    }
  }

  > input:checked {
    ~ label svg {
      fill: $accent-color;
    }
  }

  .form-inline-message {
    position: absolute;
    bottom: 0;
    left: 0;
    white-space: nowrap;

    + .form-inline-message { display: none; }
  }
}

// Rating Stars (readonly)
.rating-stars,
.rating-stars-readonly {
  display: inline-block;

  .rating-star {
    width: rem(16);
    height: rem(16);
  }

  .rating-star-empty {
    fill: rgba($body-color, 0.3);
  }

  .rating-star-full {
    fill: $accent-color;
  }
}

/* scss-combine:global/modals */
.modal-overlay {
  background-color: rgba($heading-color, 0.9);
}

.modal {
  max-width: 1060px;
  max-height: 90%;
  padding: $gutter * 2;
  overflow-y: scroll;
  background-color: $background-color;
}

.modal-content {
  position: relative;

  .modal-close {
    position: absolute;
    top: 0;
    right: 0;
    font-size: rem(16);
    line-height: 1;
  }
}

.modal-close {
  @extend %remove-button-style;
  display: flex;
  align-items: center;
  float: right;
}

.modal-close-text {
  @extend %nav-text-treatment;
  margin-right: rem(10);

  .modal-close:hover & {
    color: $accent-color;
  }
}

.modal-close-icon {
  @include basic-ease;
  width: rem(15);
  height: rem(15);
  fill: $nav-color;

  .modal-close:hover & {
    fill: $accent-color;
  }
}

.modal-wrapper.loaded .loading.visible {
  display: none;
}
/* scss-combine:global/buttons */
/* Buttons */

$button-font: $primary-font;

.button {
  @include basic-ease;
  width: rem(200);
  min-height: rem(45);
  padding: $gutter-half;
  font-family: $button-font;
  font-size: rem(13);
  font-weight: 700;
  letter-spacing: em(2, 13);
  text-transform: uppercase;

  &-disabled {
    opacity: 0.4;
  }
}

.button-primary {
  color: $primary-button-color;
  background-color: $primary-button-background-color;

  &:hover {
    color: $primary-button-color-hover;
    background-color: $primary-button-background-color-hover;
  }
}

.button-secondary {
  color: $secondary-button-color;
  background-color: $secondary-button-background-color;
  border: 1px solid $secondary-button-color;

  &:hover {
    color: $secondary-button-color-hover;
    background-color: $secondary-button-background-color-hover;
  }
}

.button-small {
  min-width: 150px;
}

.button-large {
  width: auto;
}

.button-fullwidth {
  width: 100%;

  + .button {
    margin-left: 0;
  }
}

.link-button {
  @extend %remove-button-style;
  @extend a;
}
/* scss-combine:global/loading */
.loading {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 100;
  display: none;
  background: {
    color: $background-color;
    image: url("data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 32 32' width='50' height='50' fill='#{$nav-color}'%3E %3Cpath opacity='.25' d='M16 0 A16 16 0 0 0 16 32 A16 16 0 0 0 16 0 M16 4 A12 12 0 0 1 16 28 A12 12 0 0 1 16 4'/%3E %3Cpath d='M16 0 A16 16 0 0 1 32 16 L28 16 A12 12 0 0 0 16 4z'%3E %3CanimateTransform attributeName='transform' type='rotate' from='0 16 16' to='360 16 16' dur='0.8s' repeatCount='indefinite' /%3E %3C/path%3E %3C/svg%3E ");
    position: center;
    repeat: no-repeat;
  }
  opacity: 0.7;

  &.visible {
    display: block;
  }
}

/* scss-combine:global/structure */
.site-wrap {
  display: flex;
  padding: 0;

  @include breakpoint(ms) {
    display: block;
    padding-top: rem(63); // mobile search bar
  }
}

.page-wrap {
  padding: $gutter;
  flex: 1;

  @include breakpoint(ms) {
    padding: $gutter-mobile;
  }
}


// Modules
/* scss-combine:modules/flickity */
/*! Theme: Mogul v1.1.0 */
/*! Flickity v2.0.5
http://flickity.metafizzy.co
---------------------------------------------- */

.flickity-enabled {
  position: relative;
}

.flickity-enabled:focus {
  outline: none;
}

.flickity-viewport {
  overflow: hidden;
  position: relative;
  height: 100%;
}

.flickity-slider {
  position: absolute;
  width: 100%;
  height: 100%;
}

/* draggable */

.flickity-enabled.is-draggable {
  -webkit-tap-highlight-color: transparent;
  tap-highlight-color: transparent;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
}

.flickity-enabled.is-draggable .flickity-viewport {
  cursor: move;
  cursor: -webkit-grab;
  cursor: grab;
}

.flickity-enabled.is-draggable .flickity-viewport.is-pointer-down {
  cursor: -webkit-grabbing;
  cursor: grabbing;
}

/* ---- previous/next buttons ---- */

.flickity-prev-next-button {
  position: absolute;
  top: 50%;
  width: 44px;
  height: 44px;
  border: none;
  border-radius: 50%;
  background: white;
  background: hsla(0, 0%, 100%, 0.75);
  cursor: pointer;
  /* vertically center */
  -webkit-transform: translateY(-50%);
  transform: translateY(-50%);
}

.flickity-prev-next-button:hover {
  background: white;
}

.flickity-prev-next-button:focus {
  outline: none;
  box-shadow: 0 0 0 5px #09F;
}

.flickity-prev-next-button:active {
  opacity: 0.6;
}

.flickity-prev-next-button.previous {
  left: 10px;
}

.flickity-prev-next-button.next {
  right: 10px;
}

/* right to left */
.flickity-rtl .flickity-prev-next-button.previous {
  left: auto;
  right: 10px;
}

.flickity-rtl .flickity-prev-next-button.next {
  right: auto;
  left: 10px;
}

.flickity-prev-next-button:disabled {
  opacity: 0.3;
  cursor: auto;
}

.flickity-prev-next-button svg {
  position: absolute;
  left: 20%;
  top: 20%;
  width: 60%;
  height: 60%;
}

.flickity-prev-next-button .arrow {
  fill: #333;
}

/* ---- page dots ---- */

.flickity-page-dots {
  position: absolute;
  width: 100%;
  bottom: -25px;
  padding: 0;
  margin: 0;
  list-style: none;
  text-align: center;
  line-height: 1;
}

.flickity-rtl .flickity-page-dots {
  direction: rtl;
}

.flickity-page-dots .dot {
  display: inline-block;
  width: 10px;
  height: 10px;
  margin: 0 8px;
  background: #333;
  border-radius: 50%;
  opacity: 0.25;
  cursor: pointer;
}

.flickity-page-dots .dot.is-selected {
  opacity: 1;
}


// Accounts
/* scss-combine:core/account */
// Default Variables

$account-link-color: #888 !default;
$account-light-color: #fff !default;
$account-accent-color: #5dc5e3 !default;
$account-border-color: #e4e4e4 !default;
$account-gutter: 30px !default;
$account-font-size: 15px !default;
$account-border-radius: 0 !default;
$account-text-color: #5f5f5f !default;

$account-card-background-color: #f5f5f5 !default;

$giftcard-nav-use-default-styles: true !default;

$account-breakpoint-x-small: 400px !default; // the account table columns change to full-width
$account-breakpoint-small: 620px !default; // menu changes from full-width to column
$account-breakpoint-medium: 900px !default; // menu changes from 35% to 25% width

// Internal Variables

$account-font-size-heading: $account-font-size + 2 !default;

$account-text-color-light: lighten($account-text-color,20%) !default;

$account-gutter-inner: $account-gutter / 2 !default;

$auth-gutter: $account-gutter !default;
$auth-font-size: $account-font-size !default;

// Imports

/* scss-combine:account/components/layout */
.account-wrapper {
  @extend %clearfix;
  font-size: $account-font-size;
}

.account-nav,
.account-body {
  vertical-align: top;
}

.account-body {
  @media screen and (min-width: $account-breakpoint-small) {
    float: right;
    width: 65%;
    padding-left: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 75%;
  }

  > :first-child {
    margin-top: 0;
  }

  .account-nav-hidden & {
    float: none;
    width: 100%;
    padding-left: 0;
    margin-left: 0;
  }
}

.account-nav {
  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    float: left;
    width: 35%;
    padding-right: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 25%;
  }
}

/* scss-combine:account/components/forms */
.account-wrapper {
  .form-label-text {
    display: inline-block;
    width: 100%;
    margin-bottom: 5px;
  }

  .form-field-checkbox,
  .form-field-radio {
    .form-label-text { width: auto; }
  }

  .form-field {
    margin-bottom: $account-gutter-inner;
  }

  .form-input {
    width: 100%;

    &.account-form-half {
      width: 50%;
    }
  }

  .form-actions {
    clear: both;
  }
}

.account-form-column-container {
  @extend %clearfix;

  .form-field {
    @media screen and (min-width: 480px) {
      float: left;
      width: 50%;

      &:nth-child(odd) { padding-right: $account-gutter-inner; }
      &:nth-child(even) { padding-left: $account-gutter-inner; }
    }
  }
}

/* scss-combine:account/components/table */
.account-wrapper {
  table {
    width: 100%;
    margin-top: $account-gutter-inner;
    margin-bottom: $account-gutter-inner;
  }

  td,
  th {
    padding: $account-gutter-inner;
  }
}

.account-table-tight {
  th,
  td {
    padding: 10px 0;
    border-bottom: 1px solid $account-border-color;
  }
}

/* scss-combine:account/components/list */
// Layout
// Each list item has 3 sections: thumbnail, body, and badge.

.account-items {
  margin-bottom: $account-gutter;
}

.account-item {
  position: relative;
  display: table;
  width: 100%;
  border-bottom: 1px solid $account-border-color;

  &:first-of-type {
    border-top: 1px solid $account-border-color;
  }
}

.account-item-thumbnail,
.account-item-body,
.account-item-badge,
.account-item-actions {
  padding-top: $account-gutter-inner;
  padding-bottom: $account-gutter-inner;

  @media screen and (min-width: $account-breakpoint-x-small) {
    display: table-cell;
    vertical-align: top;
  }
}

// Link
// Makes the entire item clickable

.account-item-link {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 100;
}

// Thumbnail

.account-item-thumbnail {
  min-width: 100px;

  img {
    height: auto;
    max-width: 100px;
  }
}

// Body

.account-item-body {
  width: 100%;

  @media screen and (min-width: $account-breakpoint-x-small) {
    .account-item-thumbnail + & {
      padding-left: $account-gutter-inner;
    }
  }
}

.account-item-title {
  margin-top: 3px;
  font-size: $account-font-size;

  a {
    color: inherit;
  }
}

.account-item-title-large {
  font-size: $account-font-size * 1.2;
}

.account-item-description {
  margin-top: 0;
  margin-bottom: 5px;
  color: $account-text-color;
}

.account-item-details {
  @extend %clearfix;
  font-size: $account-font-size;
  color: $account-text-color;
}

.account-item-detail {
  float: left;
  margin-top: $account-gutter-inner;
  margin-right: $account-gutter;
}

//scss-lint:disable VariableForProperty
.account-item-detail-title {
  display: block;
  margin-top: 0;
  margin-bottom: 3px;
  font-size: 12px;
  font-weight: normal;
  letter-spacing: 1px;
  color: mix($account-light-color,$account-text-color,60%);
  text-transform: uppercase;
}

// Badge

.account-item-badge,
.account-item-actions {
  white-space: nowrap;
}

/* scss-combine:account/components/header */
.account-header {
  display: table;
  width: 100%;
  margin-bottom: $account-gutter-inner;
}

.account-heading,
.account-toolbar {
  display: table-cell;
  vertical-align: baseline;
}

.account-toolbar {
  text-align: right;

  * {
    display: inline;
  }
}

.account-tool {
  position: relative;
  z-index: 10;
  display: inline-block;
  padding: 0;
  background: transparent;
  margin-left: 10px;
  cursor: pointer;
  color: inherit;
  border: 0;

  &:hover { color: $account-link-color; }

  &:focus { outline: 0; }
}

/* scss-combine:account/components/navigation */
.account-nav-list {
  padding: 0;
  margin-bottom: $account-gutter-inner;
  list-style: none;
  border: 1px solid $account-border-color;
  border-radius: $account-border-radius;
}

.account-nav-item {
  color: $account-link-color;
  border-top: 1px solid $account-border-color;

  &:first-child {
    border-top: 0;
  }

  &.is-active {
    color: $account-accent-color;
    border-left: 3px solid $account-accent-color;
  }
}

.account-nav-link {
  display: block;
  padding: 15px;
  color: inherit;
}

.account-nav-badge {
  float: right;
}

/* scss-combine:account/components/pagination */
.account-wrapper .pagination {
  margin-top: $account-gutter-inner;
}

/* scss-combine:account/components/card */
%account-card-style {
  background: $account-card-background-color;

  .form-input {
    background-color: $account-light-color;
  }
}

.account-card {
  @extend %account-card-style;
  padding: $account-gutter-inner;
  margin: $account-gutter-inner 0;
  border-radius: $account-border-radius;
}

/* scss-combine:account/components/toggle */
// Set the initial toggle state to hidden
.account-toggle-hidden {
  display: none;
}

/* scss-combine:account/components/status */
// Status
// A label used to mark the status of an item

.account-status {
  padding: 6px 10px 5px;
  margin: 0;
  font-size: 12px;
  font-weight: normal;
  line-height: 1;
  letter-spacing: 1px;
  color: $account-light-color;
  text-align: center;
  text-transform: uppercase;
  background: #8e8e8e; // TODO: Colors for each status
}

/* scss-combine:account/components/shipments */
// Shipments
// A list of shipments made for an order. Shown on the Order Details page.

.account-shipments {
  margin-bottom: $account-gutter;
}

.account-shipments-table {
  th,
  td {
    padding: 10px 0;
    text-align: left;
  }

  th {
    padding: $account-gutter-inner 0 0;
    border-top: 1px solid $account-border-color;
  }

  // Override default margins
  .account-wrapper & {
    margin-top: 0;
    margin-bottom: 0;
  }
}


/* scss-combine:account/pages/address */
// Address List

.account-page-add-address {
  .account-content {
    padding-top: $account-gutter-inner;
    border-top: 1px solid $account-border-color;
  }
}

.account-address-detail {
  display: block;
}

// Add Address

.account-address-form {
  padding-top: $account-gutter-inner;

  @media screen and (min-width: $account-breakpoint-medium) {
    .form-field {
      float: left;
      width: 50%;
      padding-right: $account-gutter-inner;

      &:nth-of-type(2n) {
        padding-right: 0;
        padding-left: $account-gutter-inner;
      }

      &:nth-of-type(2n + 1){
        clear: left;
      }
    }
  }

  .form-actions {
    padding-top: $account-gutter-inner;
  }
}

/* scss-combine:account/pages/orders-receipt */
.account-page-orders-receipt .account-content {
  padding-top: $account-gutter-inner;
  border-top: 1px solid $account-border-color;
}

// Summary

.account-receipt-summaries {
  @extend %clearfix;
}

.account-receipt-summary {
  float: left;
  width: 33%;
  padding-right: $account-gutter;
}

.account-receipt-summary-title {
  margin-top: 5px;
  margin-bottom: 10px;
  font-size: $account-font-size;
}

.account-receipt-summary-address {
  line-height: 1.5;
}

.account-receipt-detail {
  margin-bottom: 5px;
}

.account-receipt-detail-title {
  color: $account-text-color-light;
}

// Comments

.account-receipt-comments {
  white-space: pre-line;
}

// Receipt Items (products)

.account-receipt-items {
  padding-top: $account-gutter-inner;
}

.account-receipt-download {
  margin: 10px 0;
}

// Totals

.account-receipt-totals {
  display: table;
  width: 50%;
  margin-left: 50%;
}

.account-receipt-total {
  display: table-row;
  margin-bottom: $account-gutter-inner;
}

.account-receipt-total-title,
.account-receipt-total-value {
  display: table-cell;
  padding-top: $account-gutter-inner;
  padding-bottom: $account-gutter-inner;
  border-top: 1px solid $account-border-color;

  .account-receipt-total:first-of-type & {
    border-top: 0;
  }
}

.account-receipt-total-title {
  font-weight: normal;
  letter-spacing: 1px;
  color: $account-text-color-light;
}

.account-receipt-total-value {
  text-align: right;
}

/* scss-combine:account/pages/recent-items */

/* scss-combine:account/pages/returns */
// Return form

.account-return-table {
  th {
    text-align: left;

    &:last-of-type {
      text-align: right;
    }
  }

  .form-field {
    margin-bottom: 0;
  }
}

.account-return-fields {
  margin-top: $account-gutter;

  .form-field-select {
    width: 50%;
  }
}

/* scss-combine:account/pages/wishlists */
.account-wishlists {
  margin-bottom: 30px;
}

/* scss-combine:account/pages/messages */
// Listing

.account-messages-table.table-clean {
  border-bottom: 1px solid $account-border-color;

  td {
    padding-right: 0;
    padding-left: 0;
    vertical-align: top;
    border-top: 1px solid $account-border-color;
  }
}

.account-messages-table-date {
  width: 22%;
  text-align: right;
}

.account-message-subject {
  margin-top: 0;
  margin-bottom: 10px;
  font-size: $account-font-size-heading;
  font-weight: 500;

  .account-message-is-reply &:before {
    padding-right: 5px;
    content: "\21c9";
  }

  .account-message-is-read & {
    color: $account-text-color-light;
  }
}

.account-message-content {
  padding-right: $account-gutter;
  font-size: $account-font-size;
  line-height: 1.5;
  color: $account-text-color;
  white-space: pre-line;

  .account-message-is-read & {
    color: lighten($account-text-color-light,10%);
  }
}

.account-message-date {
  color: $account-text-color-light;
}

// Form

.account-messages-form-subject {
  width: 50%;
}

/* scss-combine:account/pages/download */
// List

.account-download {
  padding: $account-gutter-inner 0 0;
  margin: $account-gutter-inner 0;
  border-top: 1px solid $account-border-color;
}

.account-download-title {
  margin: 0;
  font-size: 1em;
}

.account-download-link {
  color: inherit;
  text-decoration: underline;
}

.account-download-filename {
  font-size: 1.2em;
}

.account-download-filesize {
  padding-left: 5px;
  font-weight: normal;
  color: $account-text-color-light;
}

.account-download-description {
  padding-top: 10px;
  padding-bottom: 10px;
}

.account-download-expiry {
  font-size: 0.9em;
  color: $account-text-color-light;
}

.account-download-expiry-item {
  display: block;
  padding-bottom: 5px;

  &:last-of-type {
    padding-bottom: 0;
  }
}

// Gallery

.account-downloads-gallery-image {
  text-align: center;
}

.account-downloads-gallery-thumbs {
  @extend %clearfix;
  text-align: center;
}

.account-downloads-gallery-thumb {
  display: inline-block;
  width: 10%;
  height: 10%;
  padding: 0 2px;
  vertical-align: middle;
  cursor: pointer;

  img {
    width: 100%;
    height: auto;
  }
}

/* scss-combine:account/pages/invoice */
.account-invoice-date {
  margin-top: -20px;
  font-size: $account-font-size;
}

// Address

.account-invoice-addresses {
  @extend %clearfix;
  margin-top: $account-gutter;
  margin-bottom: $account-gutter;
}

.account-invoice-address {
  float: left;
  margin-right: $account-gutter;
}

.account-invoice-address-title {
  margin-top: 5px;
  margin-bottom: 10px;
  font-size: $account-font-size;
}

.account-invoice-address-body {
  line-height: 1.5;
}

// List

.account-invoice-table {
  .account-wrapper & {
    margin-bottom: 0;
  }

  th,
  td {
    padding-right: $account-gutter-inner;
    text-align: left;
    vertical-align: top;

    &:nth-of-type(0n+3) {
      width: 10%;
    }

    &:last-of-type {
      width: 160px;
      padding-right: 0;
      text-align: right;
    }
  }

  td:first-of-type {
    width: 60px + 25px;
  }
}

.account-invoice-item-thumbnail {
  width: 60px;
  height: auto;
}

.account-invoice-item-name {
  margin-bottom: 5px;
  font-weight: bold;
}

.account-invoice-total-title {
  font-weight: bold;
}

// Totals

.account-invoice-totals {
  .account-wrapper & {
    width: auto;
    margin-top: 0;
    margin-left: auto;
  }

  th,
  td {
    padding-right: $account-gutter-inner;

    &:last-of-type {
      width: 160px;
      padding-right: 0;
      text-align: right;
    }
  }
}

/* scss-combine:account/pages/settings */
.account-page-settings {
  .alert:first-of-type {
    margin-top: 0;
  }

  .account-content {
    padding-top: $account-gutter-inner;
    border-top: 1px solid $account-border-color;
  }
}

.account-settings-form {
  @extend %clearfix;
  padding-top: $account-gutter-inner;

  .form-field {
    float: left;
    width: 50%;
    padding-right: $account-gutter-inner / 2;
    vertical-align: top;

    &:nth-of-type(2n+0) {
      padding-right: 0;
      padding-left: $account-gutter-inner / 2;
    }
  }

  .form-actions {
    padding-top: $account-gutter-inner;
  }
}


/* scss-combine:account/pages/auth-login */

.account-page-login .alert {
  max-width: 860px;
  margin-top: 0;
  margin-right: auto;
  margin-bottom: $account-gutter;
  margin-left: auto;
}

.account-login-wrapper {
  max-width: 860px;
  margin-right: auto;
  margin-left: auto;

  @media screen and (min-width: $account-breakpoint-small) {
    display: table;
    width: 100%;
    table-layout: fixed;
  }
}

.account-page-login .account-sub-heading {
  margin: 0 0 20px;
}

.account-login,
.account-create {
  @media screen and (min-width: $account-breakpoint-small) {
    display: table-cell;
    vertical-align: top;
  }
}

// Login Form

.account-login {
  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    padding: $account-gutter-inner (30px + $account-gutter-inner) $account-gutter-inner 0;
  }
}

.account-login-button {
  margin-right: $account-gutter-inner;
}

// New Account

.account-create {
  padding: $account-gutter-inner;
  background: $account-card-background-color;
}

.account-create-intro {
  &:empty {
    display: none;
  }
}

.account-create-facts {
  padding-left: 20px;
  margin-bottom: 30px;
  list-style: disc;

  li:empty {
    display: none;
  }
}

/* scss-combine:account/pages/auth-forgot */
.account-page-forgot .account-body {
  width: 100%;
  max-width: 600px;
  margin-right: auto;
  margin-left: auto;
  text-align: center;
  float: none;
}

.account-forgot-form {
  display: table;
  width: 100%;
  margin-top: $account-gutter;

  .form-field,
  .form-actions {
    display: table-cell;
    text-align: left;
    vertical-align: bottom;
  }

  .form-actions {
    width: 1px;
    padding-left: 10px;
  }
}

/* scss-combine:account/pages/auth-create */
.account-page-create {
  .account-header {
    padding-bottom: $account-gutter-inner;
    margin-bottom: $account-gutter;
    border-bottom: 1px solid $account-border-color;
  }

  .account-body {
    max-width: 860px;
    margin-right: auto;
    margin-left: auto;
  }
}

.account-create-form {
  .g-recaptcha > div {
    margin: 0 auto;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    .form-field {
      float: left;
      width: 50%;
      padding-right: $account-gutter-inner;

      &:nth-of-type(2n) {
        padding-right: 0;
        padding-left: $account-gutter-inner;
      }

      &:nth-of-type(2n + 1) {
        clear: left;
      }
    }
  }
}

.account-create-details,
.account-create-address {
  @extend %clearfix;
  padding-bottom: $account-gutter-inner;
  margin-bottom: $account-gutter;
  border-bottom: 1px solid $account-border-color;
}

.account-create-form-actions {
  text-align: center;
}

.account-create-captcha {
  display: inline-block;
  vertical-align: middle;

  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    margin-right: $account-gutter-inner;
  }
}

/* scss-combine:account/pages/auth-created */
.account-page-created .account-body {
  float: none;
  width: 100%;
  max-width: 600px;
  margin-right: auto;
  margin-left: auto;
  text-align: center;

  p {
    margin-bottom: $account-gutter;
    line-height: 1.5;
  }

  em {
    font-family: monospace;
    font-style: normal;
    font-weight: bold;
    background: $account-card-background-color;
  }
}

/* scss-combine:account/pages/auth-new-password */
.account-page-new-password .account-content {
  width: 100%;
  max-width: 400px;
  margin-right: auto;
  margin-left: auto;
}

.new-password-requirements {
  margin-bottom: $account-gutter;
  text-align: center;
}


/* scss-combine:account/pages/gift-certificate */
// (optional) gift card nav

@if ($giftcard-nav-use-default-styles) {
  .giftcard-nav {
    padding: $account-gutter * 2;
    margin-bottom: $account-gutter * 2;
    text-align: center;
  }

  .giftcard-nav-item {
    display: inline-block;
    margin: 0 1em;
    color: $account-link-color;

    &.is-active { color: $account-text-color; }
  }
}

.giftcard-heading-container {
  margin-bottom: $account-gutter * 2;
  text-align: center;
}


// Purchase

.giftcard-form-help-message {
  display: block;
  margin-top: 6px;
}

.giftcard-form-preview {
  iframe { width: 100%; }
}

.giftcard-single-theme {
  display: none;
}


// Check balance

.giftcard-balance-container {
  max-width: 600px;
  margin: 0 auto;
}

.form-giftcard-balance {
  position: relative;

  .form-field-title {
    position: absolute;
    left: -9999px;
  }

  .form-field-control {
    display: block;

    @media screen and (min-width: 600px + ($account-gutter-inner * 2)) {
      margin-right: calc(30% + #{$account-gutter-inner});
    }
  }

  .form-actions {
    @media screen and (min-width: 600px + ($account-gutter-inner * 2)) {
      position: absolute;
      top: 0;
      right: 0;
      width: 30%;
    }
  }

  .button {
    width: 100%;
  }
}


// Redeem

.giftcard-redeem-steps {
  line-height: 1.5;

  li { margin-bottom: 0.75em; }
}


/* scss-combine:account/account */
/* Account Overrides */

.account-container {
  margin: 0 auto;
  padding: 0 20px $gutter;

  @include breakpoint(l-up) {
    max-width: rem(1200);
  }
}

.account-wrapper {
  padding-top: $gutter;
  padding-bottom: $gutter;

  .form-input {
    height: 100%;
  }
}

.account-heading {
  @extend %heading-text-treatment;
  display: block;
  margin: 0 0 $gutter;
}

.account-sub-heading {
  @extend %heading-text-treatment;
}

.account-button-primary {
  width: auto;
  color: $primary-button-color;
  background-color: $primary-button-background-color;

  &:hover {
    color: $primary-button-color-hover;
    background-color: $primary-button-background-color-hover;
  }
}

.account-login-form,
.account-forgot-form,
.account-create-form {
  .form-field-title {
    color: $body-color;
  }
}

.account-forgot-form {
  .form-inline-message {
    position: absolute;
  }
}

.account-create-form,
.account-content {
  .form-field-control,
  .form-select-wrapper {
    display: block;
  }
}

.account-nav-item.is-active {
  color: $accent-color;
  border-color: $accent-color;
}

.account-page-new-password .account-content {
  margin-right: 0;
  margin-left: 0;

  .new-password-requirements {
    text-align: left;
  }
}

.account-item-link {
  z-index: 1;
  border: none;
}

.account-item-thumbnail {
  a {
    border: none;
  }
}

.account-tool {
  z-index: 1;
}

/* scss-combine:account/product */
.account-product-wrapper {
  .account-product-item {
    display: inline-block;
    position: relative;
    width: 24%;
    margin-bottom: $gutter * 2;
    padding: $gutter / 2;
    vertical-align: top;

    &:first-child {
      margin-left: -$gutter / 2;
    }

    @include breakpoint(m) {
      width: 30%;
    }

    @include breakpoint(s) {
      width: 48%;

      &:only-of-type {
        width: 100%;
      }
    }
  }

  .account-product-item-thumbnail a {
    border-bottom: none;
  }

  .account-product-item-title {
    margin-top: 0;
    margin-bottom: 0;
    line-height: 1;

    a {
      @extend %heading-text-treatment;
      font-size: rem(15);
      letter-spacing: normal;
      text-transform: none;
      border-bottom: none;

      &:hover {
        color: $accent-color;
      }
    }
  }

  .account-product-item-brand {
    display: none;
  }
}


// Components
/* scss-combine:components/carousel */
// Carousel
.homepage-carousel {
  @include height-transition;
  margin-bottom: rem(80);
  opacity: 0;
  overflow: visible;
  clear: both;

  @include breakpoint(m) {
    position: relative;
    margin-bottom: 0;
  }
}

.carousel-viewport {
  width: 100%;
  height: 100%;

  @include breakpoint(ms) {
    position: absolute;
  }
}

.flickity-viewport {
  @include maintain-ratio(1.5 1);
  @include height-transition;
  background-size: cover;

  .ratio-3-1 & {
    @include maintain-ratio(3 1);
  }

  .ratio-16-9 & {
    @include maintain-ratio(16 9);
  }

  @include breakpoint(xs) {
    @include maintain-ratio;
  }
}

.carousel-slide {
  background-position: center top;
  padding-bottom: 0;

  @include maintain-ratio(1.5 1);
  background-size: cover;

  .ratio-3-1 & {
    @include maintain-ratio(3 1);
    background-position: center center;
  }

  .ratio-16-9 & {
    @include maintain-ratio(16 9);
    background-position: center center;
  }

  @include breakpoint(xs) {
    @include maintain-ratio;
    background-position: center center;
  }
}

.carousel-mobile-image {
  display: none;

  @include breakpoint(s-up) {
    display: none;
  }
}

.carousel-slide-link {
  @include coverer;
  color: transparent;
  border: none;
}

.carousel-slide-info {
  width: rem(640);
  text-align: center;
  opacity: 0;
  text-transform: none;

  .button {
    margin-top: $gutter;
  }

  @include breakpoint(l-up) {
    @include centerer;
  }

  @include breakpoint(m) {
    width: 100%;
    padding-top: rem(70);
    padding-bottom: rem(60);
    right: 0;
    left: 0;
    margin: 0 auto;
  }
}

.carousel-slide-heading {
  @extend %heading-text-treatment;
  margin-top: 0;
  font-size: rem(32);
  line-height: 1.38;
  letter-spacing: normal;
  text-align: center;

  @include breakpoint(l-up) {
    color: $carousel-text-color;
    text-shadow: 0 rem(2) rem(24) rgba(0, 0, 0, 0.5);
  }
}

.carousel-slide-subheading {
  @extend %body-text-treatment;
  line-height: 1.69;
  letter-spacing: normal;
  text-align: center;

  @include breakpoint(l-up) {
    color: $carousel-text-color;
    text-shadow: 0 rem(2) rem(24) rgba(0, 0, 0, 0.5);
  }
}

.carousel-slide-button {
  @include basic-ease;
  display: none;
  position: absolute;
  top: 50%;
  width: rem(47);
  height: rem(47);
  bottom: inherit;
  padding: 0;
  z-index: index($elements, carousel-slide-button);
  color: $background-color;
  background-color: $accent-color;
  border: 0;
  outline: 0;
  transform: translateY(-50%);

  &:hover {
    background-color: $heading-color;
  }

  .carousel-slide.is-selected & {
    display: block;

    @include breakpoint(m) {
      display: none;
    }
  }

  .carousel-slide-icon {
    width: rem(21);
    height: rem(13);
    fill: currentColor;
  }

  &-previous {
    left: 0;

    .carousel-slide-icon {
      transform: rotate(180deg);
    }

    @include breakpoint(ms) {
      left: rem(40);
    }
  }

  &-next {
    right: 0;

    @include breakpoint(ms) {
      right: rem(40);
    }
  }
}

.flickity-page-dots {
  @include breakpoint(l-up) {
    display: none;
  }

  .dot {
    width: rem(9);
    height: rem(9);
    margin: 0 rem(5);
    background-color: $body-color;

    &.is-selected {
      background: $accent-color;
    }
  }

  .dot:first-child:nth-last-child(1) {
    display: none;
  }
}

/* scss-combine:components/header */
.main-header {
  position: relative;
  height: rem(101);
  background-color: $background-color;
  border-bottom: 1px solid $border-color;
  z-index: index($elements, header);

  @include breakpoint(ms) {
    height: rem(81);
  }
}

.main-header > .container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 100%;
  padding-right: $gutter;
  padding-left: $gutter;

  @include breakpoint(ms) {
    padding-right: $gutter-mobile;
    padding-left: $gutter-mobile;
  }
}

.main-header-tools {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  flex-grow: 1;

  > * {
    padding-left: 40px;

    @include breakpoint(ms) {
      padding-left: 0;
    }
  }

  @include breakpoint(ms) {
    flex-grow: 0;
  }
}

/* scss-combine:components/footer */
$local-footer-gutter: rem(40);

.main-footer {
  position: relative;
  z-index: index($elements, footer);
  width: 100%;
  text-align: center;
  color: $footer-text-color;
  background-color: $footer-background-color;
  overflow: hidden;
}

// Footer Top
.footer-top {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  margin-top: $local-footer-gutter;
  margin-bottom: $local-footer-gutter * 2;

  @include breakpoint(m) {
    padding-right: $gutter;
    padding-left: $gutter;
  }
}

.footer-lists {
  display: flex;
  flex: 1;
  width: 55%;
  padding-bottom: $gutter;

  @include breakpoint(m) {
    width: 100%;
  }

  @include breakpoint(xs) {
    justify-content: space-between;
  }
}

.footer-list {
  display: flex;
  flex: 1;
  flex-direction: column;
  align-items: flex-start;
  margin-right: $gutter-double;

  @include breakpoint(m) {
    margin-right: rem(100);

    &:last-of-type {
      margin-right: 0;
    }
  }

  @include breakpoint(xs) {
    flex-grow: 1;
    flex-basis: 0;
    padding-right: $gutter-half;
    margin-right: 0;

    &:last-of-type {
      padding-right: 0;
    }
  }
}

.footer-list-item {
  width: 100%;
  margin-bottom: rem(10);
  line-height: 1.62;
  text-align: left;
}

.footer-list-link {
  font-size: rem(16);
  font-weight: normal;
  letter-spacing: normal;
  text-transform: none;
  color: $footer-text-color;
  border-bottom: none;

  &:hover {
    text-decoration: underline;
  }
}
.footer-newsletter-social {
  display: flex;
  flex-direction: column;

  @include breakpoint(xs) {
    max-width: 100%;
  }
}

// Subscription Form
.subscription-form .form-field {
  position: relative;
}

.subscription-form-title {
  margin-bottom: rem(15);
  font-family: $primary-font;
  font-size: rem(16);
  font-weight: normal;
  letter-spacing: normal;
  text-align: left;
  line-height: 1.62;
  text-transform: uppercase;
  color: $footer-text-color;
}

.newsletter-input {
  width: rem(400);

  @include breakpoint(xs) {
    max-width: 100%;
  }
}

.button-newsletter {
  position: absolute;
  top: 0;
  right: 0;
  display: flex;
  width: rem(45);
  height: rem(45);
  color: $footer-text-color;
  background-color: contrast($footer-background-color, 27%);

  &:hover {
    background-color: $accent-color;
  }
}

.newsletter-arrow {
  width: rem(16);
  height: rem(10);
  fill: currentColor;
}

// Social Icons
.social-nav {
  margin-top: rem(30);
}

.social-link {
  float: left;
  margin-right: $gutter/2;

  a {
    color: $footer-text-color;
    border-bottom: none;

    &:hover {
      color: $accent-color;
    }
  }

  .icon-social {
    width: rem(18);
    height: rem(18);
    fill: currentColor;
  }
}

// Footer Bottom
.footer-bottom {
  border-top: 1px solid rgba($border-color, 0.2);

  @include breakpoint(m) {
    padding-right: $gutter;
    padding-left: $gutter;
  }
}

.footer-credits-copyright {
  margin-top: $local-footer-gutter;
  margin-bottom: $local-footer-gutter;
  font-size: rem(15);
  color: rgba($footer-text-color, 0.6);
}

.footer-credits-copyright a {
  font-family: inherit;
  font-size: inherit;
  font-weight: inherit;
  letter-spacing: inherit;
  text-transform: inherit;
  color: inherit;
  border: inherit;

  &:hover {
    text-decoration: underline;
  }
}

// Payment Icons
.footer-payment-icons-list {
  display: flex;
  justify-content: center;
}

.footer-payment-icons {
  margin-top: $local-footer-gutter;
  margin-bottom: $local-footer-gutter;
}

.footer-payment-icon {
  padding-right: rem(5);
  padding-left: rem(5);
  vertical-align: middle;
  fill: currentColor;

  &.visa {
    width: rem(35);
    height: rem(14);
  }

  &.mastercard {
    width: rem(39);
    height: rem(23);
  }

  &.american-express {
    width: rem(42);
    height: rem(14);
  }

  &.discover {
    width: rem(51);
    height: rem(8);
  }

  &.paypal {
    width: rem(43);
    height: rem(12);
  }

  &.apple-pay {
    width: rem(40);
    height: rem(24);
  }
}

/* scss-combine:components/branding */
.branding {
  display: flex;
  flex-direction: column;
  justify-content: center;
  height: rem(100);
  margin-right: rem(40);
  overflow: hidden;

  @include breakpoint(tablet-up) {
    flex: 0 0 rem(230);
  }

  @include breakpoint(ms) {
    height: rem(80);
    margin-right: 0;
    text-align: center;
  }
}

.branding-logo {
  line-height: 0;
  border-bottom: none;
}

.branding-text {
  max-width: rem(350);
  margin: 0;
  padding: $gutter-mobile 0;

  @include breakpoint(ms) {
    max-width: 100%;
  }

  @include breakpoint(xs) {
    padding: $gutter-mobile rem(5);
  }
}

.branding-text a {
  @extend %heading-text-treatment;
  display: block;
  font-size: rem(18);
  letter-spacing: 0;
  border: 0;

  &:hover {
    border-bottom-color: transparent;
  }

  @include breakpoint(ms) {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
  }
}

.branding-logo-image {
  max-width: rem(230);
  max-height: rem(100);

  @include breakpoint(ms) {
    max-height: rem(80);
  }
}

/* scss-combine:components/header/currency-selector */
.currency-selector-wrapper {
  @extend %nav-text-treatment;
  display: flex;
  align-items: center;
  min-width: 0;
  padding: {
    top: 0;
    right: 0;
    bottom: 0;
  }
  border: none;

  select {
    left: rem(20);
    background: none;
    border: 0;
    opacity: 0;
  }

  .form-selected-text {
    padding-right: rem(6);
  }

  &::after {
    display: block;
    position: static;
    margin-right: rem(1);
    margin-bottom: rem(2);
    transform-origin: left top;
