}

.nav-submenu-item-link {
  flex: 1 1 80%;
  font-size: inherit;
  font-weight: normal;
  text-transform: none;
  color: $body-color;
  border-bottom: none;

  &:hover,
  .nav-menu-item-row-active & {
    color: $accent-color;
  }
}

// Pages
.nav-pages {
  order: 3;
  clear: both;
  padding-top: rem(40);

  .sidebar-pages-first & {
    order: 0;
    padding: rem(30) 0 rem(40);
  }
}

/* scss-combine:components/nav-mobile */
.nav-mobile-toggle {
  @extend %remove-button-style;
  position: relative;
  top: rem(3);
  flex-basis: 7%;
  text-align: left;

  @include breakpoint(tablet-up) {
    display: none;
  }

  @include breakpoint(s-up) {
    padding-right: $gutter-half;
  }

  @include breakpoint(ms) {
    flex-basis: auto;
    padding-left: 0;
  }
}

.nav-mobile-icon {
  width: rem(23);
  height: rem(15);
  fill: $nav-color;
}

@include breakpoint(ms) {
  .nav-mobile-close {
    margin-bottom: $gutter-mobile;
  }

  .nav-menu-item-top-row,
  .nav-submenu-item-top-row {
    width: auto;
  }

  .nav-menu-item-link,
  .nav-submenu-item-link {
    font-size: rem(16);
    color: $background-color;

    &:hover {
      color: $background-color;
      opacity: 0.8;
    }
  }

  .nav-menu-item-top-row,
  .nav-submenu-item-top-row {
    .submenu-toggle {
      border: 1px solid rgba($background-color, 0.2);
      box-sizing: border-box;
    }
  }

  .nav-menu-item-row-active,
  [data-menu-item-breadcrumb] {
    .nav-menu-item-link,
    .nav-submenu-item-link {
      color: $background-color;
    }

    .submenu-toggle {
      background: transparent;
    }
  }

  .submenu-toggle {
    color: $background-color;

    .nav-menu-item-row-active & {
      color: $accent-color;
      background: $background-color;
    }
  }

  .submenu-toggle-icon {
    fill: currentColor;
  }

  .nav-submenu {
    margin-left: 0;
    padding-left: rem(10);
  }

  .nav-submenu-item {
    width: 100%;
  }
}

/* scss-combine:components/product/products-grids */
$grid-image-crop: stencilString('grid_image_crop');

.products-section-title {
  margin-bottom: $gutter * 2;
  font-size: rem(30);
  text-align: center;

  &.category-page-title {
    margin-bottom: $gutter;
  }
}

.products-section-image {
  width: 100%;
  height: rem(230);
  margin-bottom: $gutter;
  background-position: center center;
  background-size: cover;
}

.products-section-description {
  line-height: 1.62;
  text-align: center;
}

.products-section-grid {
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
  width: 100%;
}

.product-item {
  position: relative;
  width: 22%;
  margin-bottom: $gutter;

  @include breakpoint(m) {
    width: 30%;
  }

  @include breakpoint(s) {
    width: 47%;

    &:only-of-type {
      width: 100%;
    }
  }
}

.product-item-empty {
  margin: 0;
}

.product-item-image {
  position: relative;
  max-width: 100%;
  margin: 0 auto;
  overflow: hidden;

  &.aspect-one-by-one {
    @include maintain-ratio(1 1);
  }

  &.aspect-four-by-five {
    @include maintain-ratio(4 5);
  }

  &.aspect-two-by-three {
    @include maintain-ratio(2 3);
  }

  &.aspect-three-by-two {
    @include maintain-ratio(3 2);
  }

  img {
    display: none;
  }

  a {
    @include coverer;
    margin: 0 auto;
    overflow: hidden;
    background: 50% 50% transparent no-repeat;
    background-size: $grid-image-crop;
    border: 0;
  }
}

.product-item-title {
  position: relative;
  bottom: 0;
  z-index: index($elements, product-title);
  margin-top: rem(10);
  margin-bottom: 0;
  line-height: 1;
  background-color: $background-color;

  a {
    @extend %heading-text-treatment;
    font-size: rem(18);
    letter-spacing: normal;
    text-transform: none;
    border-bottom: none;

    &:hover {
      color: $accent-color;
    }
  }
}

.product-item-price {
  padding: rem(10) 0;
}

.product-item-compare {
  @include custom-ease(0.3s);
  position: relative;
  bottom: 0;
  font-size: rem(14);

  .form-label-text {
    display: flex;
    align-items: center;
    padding-top: rem(10);
    background-color: $background-color;
  }

  @include breakpoint(m) {
    display: none;
  }
}

.product-item-details {
  @include custom-ease(0.3s);
  position: relative;
  bottom: 0;
  background-color: $background-color;

  .rating-stars {
    padding-top: rem(10);
  }

  @include breakpoint(tablet-up) {
    .product-item:hover & {
      bottom: rem(52);
    }

    .product-listing-quick-view .product-item:not(.product-item-sold-out):hover & {
      bottom: rem(100); // button height + padding
    }

    .compare-item:hover & {
      bottom: rem(82);
    }
  }
}

.button-quick-view,
.button-cart-action {
  position: absolute;
  width: 100%;
  opacity: 0;
  visibility: hidden;
  transition: none;

  @include breakpoint(tablet-up) {
    .product-item:hover & {
      opacity: 1;
      visibility: visible;
      transition: opacity 0.15s $custom-ease-transition 0.15s;
    }
  }
}

.button-quick-view {
  .button-cart-action + & {
    margin-top: rem(10);
    margin-left: 0;
    transform: translateY(rem(45));
  }
}

// Featured Products

.products-featured-item {
  position: relative;
  width: 32%;

  @include breakpoint(s) {
    &:first-of-type {
      width: 100%;
    }

    width: 48%;
  }
}

.price-retail {
  text-decoration: line-through;
}

.price-with-retail {
  color: $accent-color;
}

.tag-sale {
  @extend %nav-text-treatment;
  position: absolute;
  top: 0;
  right: 0;
  z-index: index($elements, product-tag);
  padding: rem(10) rem(18);
  text-transform: uppercase;
  color: $primary-button-color;
  line-height: 1.43;
  background-color: $accent-color;

  &:hover {
    color: $primary-button-color;
  }
}

.tag-sold-out {
  @extend %nav-text-treatment;
  position: absolute;
  top: 0;
  right: 0;
  z-index: index($elements, product-tag);
  padding: rem(10) rem(18);
  text-transform: uppercase;
  color: $primary-button-color;
  line-height: 1.43;
  background-color: $nav-color;

  &:hover {
    color: $primary-button-color;
  }
}

// Collection
.category-divider {
  width: 100%;
  height: rem(1);
  margin: $gutter 0;
}

.category-page-header {
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  justify-content: space-between;
  width: 100%;

  .category-product-total-refine {
    display: flex;
    align-items: center;

    @include breakpoint(s-up) {
      flex-basis: 50%;
    }
  }

  .category-product-total {

    @include breakpoint(ms) {
      padding-left: $gutter-half;
    }
  }

  .filter-sort {
    display: flex;
    align-items: center;
    justify-content: flex-end;

    @include breakpoint(s-up) {
      flex-basis: 50%;
    }

    .filter-title {
      padding-right: $gutter-half;
      font-size: rem(14);
      font-weight: normal;
      letter-spacing: normal;
    }

    .form-select-wrapper {
      display: block;
      width: rem(160);
      min-width: 0;
      box-sizing: border-box;
    }
  }
}

.product-listing {
  padding-top: $gutter;
}

.listing-pagination {
  width: 100%;
  padding-bottom: $gutter-mobile;
  text-align: center;
}

.listing-pagination-link {
  font-size: rem(14);
  font-weight: normal;
  letter-spacing: rem(1.5);
  color: $nav-color;
  border-bottom: none;

  &.active,
  &:hover {
    color: $accent-color;
  }

  &.active {
    font-weight: 700;
  }

  &.previous {
    float: left;
  }

  &.next {
    float: right;
  }
}

//filters

.filter-selected {
  display: flex;
  margin-top: $gutter;
}

.filter-selected-item {
  float: left;
  margin-right: $gutter;
  margin-bottom: $gutter;
  padding: rem(5) rem(10);
  background: $border-light-color;
  border: 1px solid $border-color;
}

.filter-selected-item-link {
  font-size: rem(12);
  font-weight: 400;
  letter-spacing: em(1.5, 12);
  line-height: 2.17;
  color: $nav-color;
  border: none;
}

.filter-selected-item-icon {
  width: rem(8);
  height: rem(8);
  margin-left: $gutter-half;
  fill: currentColor;
}

.filter-reset {
  width: auto;
  padding: rem(5) rem(10);
  line-height: 2.17;
}

/*
 * Related Products
 */
.products-related {
  order: 5;
  flex-basis: 100%;
  padding-top: $gutter-double;
  border-top: 1px solid $border-color;

  @include breakpoint(s) {
    padding-top: $gutter-mobile;
    margin-right: rem(20);
    margin-left: rem(20);
    border-top: none;
  }
}

/* scss-combine:components/product/product-images */
.product-image {
  position: relative;
  width: 50%;
  margin-bottom: $gutter;
  visibility: hidden;

  @include breakpoint(m) {
    display: block;
  }

  @include breakpoint(s) {
    width: 100%;
  }
}

.product-main-image-slide {
  width: 100%;
}

.product-image-thumbnails {
  position: absolute;
  bottom: 0;
  left: 90px;
  width: calc(100% - 180px); // position between arrows
  height: rem(120);
  margin-top: rem(15);

  &-hidden {
    display: none;
  }
}

.product-thumbnails-slide {
  width: rem(120);
  height: rem(120);
  margin-right: $gutter-half;
  background-position: center;
  background-size: cover;

  &.is-selected {
    @extend %box-shadow-inner;
    border: 1px solid $accent-color;
  }
}

.product-thumbnails-arrow {
  @extend %remove-button-style;
  position: absolute;
  bottom: 0;
  width: rem(75);
  height: rem(120);
  color: $nav-color;
  background-color: $background-color;
  border: 1px solid $border-color;
  cursor: pointer;

  &:hover {
    background-color: $accent-color;
    color: $background-color;
    border-color: $accent-color;
  }

  &-previous {
    left: 0;
  }

  &-next {
    right: 0;
  }

  &-hidden {
    display: none;
  }
}

.product-thumbnails-arrow-icon {
  width: rem(22);
  height: rem(14);
  fill: currentColor;

  .product-thumbnails-arrow-previous & {
    transform: rotate(180deg);
  }
}

/* scss-combine:components/product/product-details */
$local-gutter: rem(40);

.product-details {
  width: 50%;
  padding-top: $gutter-double;
  padding-left: $gutter;
  margin-bottom: $gutter-double;

  @include breakpoint(s) {
    width: 100%;
    padding: $gutter-mobile;
    margin-bottom: $gutter-mobile * 2;
  }

  @include breakpoint(xs) {
    padding: $gutter-half;
  }
}

.product-details .star {
  width: rem(16);
  height: rem(16);
}

.product-details-rating {
  display: flex;
  align-items: center;

  .rating-stars {
    margin-right: rem(15);
  }

  .rating-star {
    vertical-align: text-top;
  }

  .rating-star-full {
    fill: $heading-color;
  }

  .rating-star-empty {
    fill: $body-color;
  }
}

.product-details .review-link {
  border-bottom-color: transparent;

  &:hover {
    border-bottom-color: $accent-color;
  }
}

.product-brand {
  display: inline-block;
  margin-top: $gutter-half;
  font-size: rem(16);
  font-weight: 400;
  letter-spacing: em(1, 16);
  color: $nav-color;
  border-bottom-color: transparent;

  &:hover {
    border-bottom-color: $nav-color;
  }
}

.product-title {
  @extend %heading-text-treatment;
  margin-top: rem(8);
  font-size: rem(30);
}

.product-details-price {
  font-size: rem(23);

  .product-price-saved {
    margin-left: $gutter-half;
    font-size: rem(16);
    letter-spacing: em(1, 16);
    text-transform: uppercase;
    color: $nav-color;
  }
}

.product-details-description-top {
  padding-top: $local-gutter;
  padding-bottom: $local-gutter;
  border-bottom: 1px solid $border-color;

  @include breakpoint(s) {
    padding-top: $gutter-mobile;
    padding-bottom: $gutter-mobile;
  }
}

.product-description-read-more {
  display: none;
  margin-top: $gutter-half;
}

.product-details-description-short {
  opacity: 0;

  &.loaded {
    opacity: 1;
  }

  &.loaded + .product-description-read-more {
    display: inline-block;
  }

  p {
    margin: 0;
  }
}

.product-options {
  padding-top: $local-gutter;

  @include breakpoint(s) {
    padding-top: $gutter-mobile;
  }
}

.product-details .price-rrp {
  text-decoration: line-through;

  + .price-value {
    color: $accent-color;
  }
}

.product-extra-details {
  padding-top: rem(30);
}
/* scss-combine:components/product/product-single */
.product .page-wrap {
  @include breakpoint(s) {
    padding: 0;
  }
}

.product .breadcrumbs {
  @include breakpoint(s) {
    padding: $gutter-mobile;
  }
}

.single-product {
  display: flex;
  flex-wrap: wrap;

  @include breakpoint(s) {
    flex-direction: column;
  }
}

.product-details-top {
  margin-bottom: $local-gutter;
  border-bottom: 1px solid $border-color;
}

.product-add-to-cart {
  position: relative;
  display: flex;
}

// Wishlist
.product-wishlist-add {
  margin-left: $gutter/2;
}

.button-wishlist-add {
  position: relative;
  width: rem(51);
  color: $accent-color;

  .star-wishlist {
    fill: currentColor;
  }
}

.add-to-wishlists {
  @extend %revealer-scale;
  display: none;
  position: absolute;
  top: rem(55);
  right: 0;
  width: rem(250);
  background-color: $background-color;

  li {
    display: flex;
    align-items: center;
    height: rem(45);
    padding-right: rem(15);
    padding-left: rem(15);
    border-top: 1px solid $border-color;
    border-right: 1px solid $border-color;
    border-left: 1px solid $border-color;

    &:last-of-type {
      border-bottom: 1px solid $border-color;
    }

    &:hover {
     background-color: $border-light-color;
    }

    a {
      color: $nav-color;
      font-weight: 400;
      border-bottom: none;
    }
  }
}

.product-view {
  position: absolute;
  bottom: rem(-35);
  left: $gutter-half;
}

/* scss-combine:components/product/product-options */
.form-field-title {
  font-size: rem(14);
  color: lighten($nav-color, 30%);
}

.form-field-options,
.form-field-customizations {
  margin-bottom: $gutter;
}

// Pick list
.product-picklist-radio {
  width: 0;
  height: 0;
  opacity: 0;
}

.product-picklist-item {
  position: relative;
  display: block;
}

.product-picklist-radio {
  position: absolute;
  top: 50%;
  margin-top: -7px;
  line-height: 14px;
}

.product-picklist-item-body {
  position: relative;
  display: table;
  width: 100%;
  min-height: rem(60);
  padding: rem(6) rem(8) rem(6) rem(50);
  border: 1px solid transparent;
  cursor: pointer;

  &:hover {
    z-index: 1;
  }

  [type="radio"]:checked ~ &,
  [type="radio"]:hover ~ & {
    z-index: 2;
    color: $accent-color;
  }

  [type="radio"]:checked ~ & {
    border-color: $accent-color;
  }
}

.product-picklist-image-wrap {
  position: absolute;
  top: 8px;
  bottom: 8px;
  left: 0;
  width: 40px;
  overflow: hidden;

  [type="radio"]:checked ~ &,
  [type="radio"]:hover ~ & {
    border: 1px solid $accent-color;
  }
}

.product-picklist-image {
}

.product-picklist-label-text {
  display: table-cell;
  line-height: 1.4;
  color: inherit;
  vertical-align: middle;
}

// Swatch

.swatch-value {
  font-weight: 600;
  color: $nav-color;
}

.form-field-swatch {
  .form-field-control {
    @extend %clearfix;
    display: block;
    margin-top: $gutter / 2;
  }

  .form-label-text {
    display: none;
  }

  .swatch-pattern-expanded {
    position: absolute;
    bottom: calc(100% + 10px);
    left: 50%;
    padding: 0.25rem;
    border: 1px solid $border-color;
    background-color: $background-color;
    transform: translateX(-50%) scale(0);
    transform-origin: 50% 100%;
    visibility: hidden;
    opacity: 0;
    transition: all 0.1s ease;

    img {
      display: block;
      width: rem(200);
      max-width: none;
    }
  }

  .swatch-wrap {
    position: relative;
    display: inline-block;

    .form-label-text {
      @extend .visually-hidden;
    }

    &:hover {
      .swatch-pattern-expanded {
        transform: translateX(-50%) scale(1);
        visibility: visible;
        opacity: 1;
      }
    }
  }

  // The block that the swatch sits within.
  .swatch {
    position: relative;
    display: block;
    width: rem(60);
    height: rem(45);
    margin-right: rem(11);
    border: 1px solid $input-border-color;
  }

  // Blocks displaying the pattern / colors
  .swatch-color {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;

    &.secondary {
      left: 50%;
    }

    &.tertiary {
      top: 50%;
      left: 50%;
    }
  }

  .swatch-radio {
    position: absolute;
    z-index: 1;
    width: 100%;
    height: 100%;
    cursor: pointer;
    opacity: 0;

    // Selected Swatch Item
    &:checked + .swatch {
      border-color: $accent-color;
      box-shadow: inset 0 0 0 2px rgba($background-color, 1);

      .swatch-color {
        top: 3px;
        right: 3px;
        bottom: 3px;
        left: 3px;

        &.secondary {
          left: 50%;
        }

        &.tertiary {
          left: 50%;
          top: 50%;
        }
      }
    }
  }
}

// rectangles
.form-field-rectangle {
  @extend %clearfix;

  .rectangle {
    position: relative;
    display: inline-block;
    float: left;
    margin: 0 5px 5px 0;
  }

  .rectangle-text {
    @include basic-ease;
    display: block;
    min-width: rem(60);
    height: rem(45);
    padding: rem(8) rem(15);
    font-size: rem(12);
    line-height: 2.17;
    text-align: center;
    border: {
      style: solid;
      width: 1px;
      color: $border-color;
    }
    cursor: pointer;
  }

  .form-input {
    width: 0;
    height: 0;
    opacity: 0;

    // Selected Item
    &:checked + .rectangle-text {
      color: $accent-color;
      background-color: $background-color;
      border-color: $accent-color;
    }

    &:hover + .rectangle-text {
      color: $background-color;
      background-color: $accent-color;
      border-color: $accent-color;
    }
  }
}

.product-event-bounds {
  display: block;
  margin-top: rem(5);
  font-size: rem(14);
  color: lighten($nav-color, 40%);
}

.product-alert .alert-success {
  @extend %nav-text-treatment;
  width: 100%;
  margin-bottom: $gutter;
  padding: $gutter / 3 $gutter;
  text-align: center;
  color: $heading-color;
  background-color: $border-light-color;
}

.option-unavailable {
  opacity: 0.5;

  .form-label-text { text-decoration: line-through; }
}

/* scss-combine:components/product/product-tabs */
.product-tabs {
  width: 100%;
  margin-top: $gutter;
  margin-bottom: $gutter-double;
  text-align: center;
  border-top: 1px solid $border-color;

  @include breakpoint(s) {
    padding: 0 $gutter-mobile;
    margin-top: $gutter-mobile;
  }

  @include breakpoint(xs) {
    order: 4;
    padding: 0;
    margin-top: $gutter-mobile;
  }

  .product-tabs-wrapper {
    position: relative;
    display: flex;
    flex: 1 0 auto;
    align-items: flex-start;
    justify-content: center;

    @include breakpoint(xs) {
      flex-direction: column;
      text-align: left;
    }
  }
}

.product-tab-title {
  padding: 0 $gutter/2;

  &.active {
    border-top-color: $accent-color;

    .product-tab-link {
      font-weight: 700;
      color: $heading-color;
    }
  }

  @include breakpoint(xs) {
    position: relative;
    width: 100%;
    border-bottom: 1px solid $border-color;
  }
}

.product-tab-link {
  display: block;
  padding: $gutter-half 0 0;
  font-size: rem(16);
  font-weight: 400;
  letter-spacing: em(1, 16);
  color: $nav-color;
  border-top: 5px solid transparent;
  border-bottom: none;

  &.active {
    font-weight: 700;
    color: $heading-color;

    @include breakpoint(xs-up) {
      border-top-color: $accent-color;
    }
  }
}

.num-reviews {
  &:before {
    content: attr(data-num-reviews);
  }

  &[data-num-reviews="()"] {
    display: none;
  }
}

.product-tab-toggle {
  display: inline-flex;
  position: relative;
  top: rem(-5);
  height: em(20, 16);
  width: em(20, 16);
  margin-left: rem(10);
  cursor: pointer;

  @include breakpoint(xs-up) {
    display: none;
  }
}

.product-tab-toggle-icon {
  position: relative;
  height: em(2, 16);
  width: 100%;
  margin: auto;

  &:before,
  &:after {
    content: '';
    position: absolute;
    display: block;
    height: em(2, 16);
    width: em(12, 16);
    background-color: $accent-color;
    transition: transform 0.1s;
  }

  &:before {
    transform: rotate(90deg);

    .active & {
      transform: rotate(0);
    }
  }
}

.product-tabs-section {
  display: none;

  &.active {
    display: block;
  }

  &::before {
    // This for bc-tabs' slide functionality
    display: none;
    content: "slide";

    @include breakpoint(xs-up) {
      content: "";
    }
  }

  @include breakpoint(xs) {
    padding-bottom: $gutter-mobile;
  }
}

.product-tabs-content {
  @include custom-ease;
  position: absolute;
  left: 0;
  width: 100%;
  margin-top: rem(80);

  @include breakpoint(xs) {
    position: static;
    margin-top: $gutter-mobile;
  }
}

.product-review-title,
.product-details-title,
.product-warranty-title {
  @extend %heading-text-treatment;
  width: 100%;
  font-size: rem(26);
  text-align: center;

  @include breakpoint(xs) {
    display: none;
  }
}

/*
 * Description
 */

.product-tabs-description {
  text-align: left;
}

/*
 * Reviews
 */

.product-tabs-reviews {
  position: relative;
  font-size: 0;
  text-align: left;

  &.active {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
  }

  .review-link {
    position: absolute;
    top: $gutter-half;
    right: 0;
    padding: 0;

    @include breakpoint(xs) {
      right: $gutter-half;
    }
  }

  .product-tabs-no-reviews {
    @extend %body-text-treatment;
    margin: $gutter 0;
  }

  .no-review-link {
    margin-left: rem(5);
    padding: 0;
  }

  @include breakpoint(xs) {
    position: static;
  }
}

.product-tab-review {
  display: inline-block;
  width: 50%;
  margin: $gutter 0;
  vertical-align: top;

  @include breakpoint(xs) {
    width: 100%;
  }

  .product-rating {
    line-height: normal;
  }
}

.product-tab-review-name {
  margin-top: $gutter-half;
  font-size: rem(16);
  font-weight: 400;
  letter-spacing: em(1, 16);
  text-transform: uppercase;
  color: $nav-color;
}

.product-tab-review-title {
  @extend %heading-font;
  margin-top: $gutter-half;
  margin-bottom: $gutter-half;
  font-size: rem(22);
  color: $heading-color;
}

.product-tab-review-description {
  @extend %body-text-treatment;
  margin-top: rem(20);
}

#modal-review-form {
  display: none;
}

/*
 * Details Tab
 */

.product-tabs-details {
  text-align: left;
}

.product-tab-details-item {
  @extend %input-text-treatment;
  font-size: rem(18);
  letter-spacing: normal;
  text-transform: uppercase;
}

.product-tab-details-item-name {
  font-weight: 700;
}

.product-tab-details-hidden {
  display: none;
}

#bulk-pricing-modal {
  display: none;
}

.modal-bulk-pricing {
  max-width: rem(550);

  .modal-header-title {
    @extend %heading-text-treatment;
    color: $heading-color;
  }
}

.modal-close {
  @extend %remove-button-style;
  @extend %nav-text-treatment;
  display: flex;
  align-items: center;
  padding: rem(25) rem(25) 0;
  font-size: rem(14);
  color: $heading-color;
}

.bulk-pricing-close-icon {
  width: rem(15);
  height: rem(15);
  margin-left: rem(10);
  color: currentColor;
}

/*
 * Video Tab
 */

.product-videos-list {
  margin-bottom: $gutter-double;

  @include breakpoint(xs) {
    margin-bottom: 0;
  }
}

.product-video-container {
  margin-bottom: $gutter;
}

/* scss-combine:components/product/review-modal */
.modal-leave-review {
