  width: 100%;
  margin-top: $gutter-half;

  @include breakpoint(ms) {
    margin: $gutter 0;
    text-align: left;
  }
}

.quick-search-list-content-title {
  font-size: rem(14);
  text-transform: uppercase;
  letter-spacing: em(0.9, 16);
}

.quick-search-list-content {
  flex-basis: 100%;
  text-align: left;

  @include breakpoint(ms) {
    padding: $gutter 0 0;
  }
}

.quick-search-list-product + .quick-search-list-content {
  flex-basis: 25%;
  padding: $gutter;
  border-left: 1px solid $border-color;

  @include breakpoint(ms) {
    padding: $gutter 0 0;
    border: 0;
  }
}

.quick-search-content-block {
  max-height: rem(200);
  padding-top: $gutter-half;
  padding-bottom: $gutter-half;
  overflow: hidden;
  border-bottom: 1px solid $border-color;

  &:last-of-type {
    border-bottom: none;
  }
}

.quick-search-content-title {
  margin: 0 0 rem(5);
  line-height: normal;

  a {
    @extend %heading-text-treatment;
    font-size: rem(16);
    letter-spacing: normal;
    text-transform: none;
    border-bottom: none;
  }
}

.quick-search-content-summary {
  margin: 0;
}

// Quick Search Overlay
.quick-search-overlay {
  @include custom-ease;
  display: none;
  position: fixed;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
  z-index: index($elements, search);
  width: 100%;
  max-width: 100%;
  padding: 0;
  background-color: rgba($heading-color, 0.9);

  &.animating,
  &.visible {
    display: block;
    opacity: 1;
  }
}

.search-close {
  @extend %remove-button-style;
  position: absolute;
  top: rem(12);
  right: rem(12);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0;
  width: rem(22);
  height: rem(22);
  color: $body-color;
  background-color: $border-color;
  border-radius: 50%;
  opacity: 0;
  visibility: hidden;

  .search-close-icon {
    width: rem(8);
    height: rem(8);
    color: inherit;

    @include breakpoint(m) {
      position: relative;
      top: rem(-2);
    }
  }

  &.visible {
    opacity: 1;
    visibility: visible;
    transition: opacity 0.15s $custom-ease-transition 0.15s;
  }

  @include breakpoint(m) {
    display: block;
  }

  @include breakpoint(ms) {
    top: $gutter;
    right: $gutter;
  }
}

.quick-search-no-results {
  width: 100%;
  text-align: center;
}

/* scss-combine:core/swatch */
// none swatch value for non-required swatches
.form-field-swatch {
  .swatch-none {
    .swatch {
      overflow: hidden;
      border: 1px solid rgba(#000, 0.15);

      svg {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
      }
    }
  }
}


// Pages
/* scss-combine:pages/page */
.page-container {
  margin: 0 auto;
  padding: 0 rem(20) $gutter-double;

  @include breakpoint(l-up) {
    max-width: rem(1200);
  }

  @include breakpoint(m) {
    padding-bottom: $gutter;
  }
}

.page-narrow {
  @include breakpoint(l-up) {
    max-width: rem(850);
  }
}

.page-header {
  margin-bottom: $gutter-double;
  text-align: center;

  @include breakpoint(m) {
    margin: rem(45) 0;
  }

  @include breakpoint(s) {
    margin: $gutter 0;
  }
}

.page-heading {
  margin-top: 0;
}

.page-form {

  .form-field {
    margin-top: $gutter-half;

    .form-field-title {
      color: $body-color;
      opacity: 0.6;
    }

    textarea {
      height: auto;
    }
  }

  .form-actions {
    margin-top: $gutter-mobile;
  }
}

/* scss-combine:pages/contact */
/* Contact Overrides */

.page_contact_form {
  .page-container {
    @include breakpoint(xs) {
      padding: 0;
    }
  }
}

.button-contact-submit {
  color: $primary-button-color;
  background-color: $primary-button-background-color;

  &:hover {
    color: $primary-button-color-hover;
    background-color: $primary-button-background-color-hover;
  }
}

#form-contact-us .g-recaptcha {
  margin-top: $gutter;

  @include breakpoint(xs) {
    transform: scale(0.90);
    transform-origin: 0 0;
  }
}

/* scss-combine:pages/search */
// Search specific styling

.search .products-section-title {
  margin-bottom: rem(50);
}

.search-tabs {
  display: flex;
  justify-content: center;
  margin-bottom: $gutter-mobile * 2;
  text-align: center;
  border-top: 1px solid $border-color;
}

.search-tab-title {
  margin-right: $gutter/2;
  margin-left: $gutter/2;
  padding-top: 0;
}

.search-tab-link {
  display: block;
  padding: $gutter/2 0 0;
  font-size: rem(16);
  font-weight: 400;
  letter-spacing: em(1, 16);
  color: $nav-color;
  border-top: 5px solid transparent;
  border-bottom: none;

  &.active {
    font-weight: 700;
    color: $heading-color;
    border-top-color: $accent-color;
  }
}

.search-product-listing {
  display: none;

  &::before {
    display: none;
    content: "slide";
  }
}

.search-content-listing {
  display: none;

  &::before {
    display: none;
    content: "slide";
  }
}

// Content Listing

.search-content {
  padding-top: $gutter*2;
  padding-bottom: $gutter*2;
  border-bottom: 1px solid $border-color;

  &:last-of-type {
    border-bottom: none;
  }
}

.search-content-type {
  font-size: rem(16);
  letter-spacing: em(1, 16);
  text-transform: uppercase;
  color: $nav-color;
}

.search-content-title {
  @extend %heading-text-treatment;
  margin-top: $gutter/2;
  font-size: rem(26);
  font-weight: 700;
  line-height: 1.54;
}

.search-content-description {
  margin-bottom: $gutter/2;
}

.search-listing-no-results {
  padding-top: $gutter;
  text-align: center;
}

.search-results-group {
  padding: $gutter-mobile;
  margin: $gutter 0;
  background: lighten($border-color, 8%);

  .search-results-title {
    margin-top: 0;
  }
}

.search-results-list {
  display: flex;
  align-items: flex-start;

  @include breakpoint(xs) {
    flex-direction: column;
  }
}

.search-results-item {
  flex: 1;
  padding-right: $gutter-mobile;
  line-height: 1.5;

  &:last-of-type {
    padding-right: 0;
  }

  @include breakpoint(xs) {
    padding: 0;
    margin-bottom: $gutter-half;

    &:last-of-type {
      margin-bottom: rem(5);
    }
  }
}

/* scss-combine:pages/cart */
$local-gutter: rem(50);

.cart-title {
  margin: 0 auto;
  padding-top: $gutter;
  padding-bottom: $local-gutter;
  font-size: rem(30);
  text-align: center;
  border-bottom: 1px solid $border-color;
}

/*
 * Cart Contents
 */

.cart-item-headers {
  display: flex;
  padding-top: rem(75);

  @include breakpoint(ms) {
    display: none;
  }
}

.cart-item-header {
  padding-right: $gutter-mobile;
  padding-bottom: $gutter-mobile;
  font-size: rem(16);
  font-weight: 700;
  letter-spacing: em(1, 16);
  line-height: normal;
  text-transform: uppercase;
  color: $heading-color;
  border-bottom: 1px solid $border-color;
}

.cart-item-header {
  padding-right: $gutter-mobile;
}

.cart-item-price-column,
.cart-item-total-column,
.cart-item-remove-column {
  display: flex;
  align-items: center;
}

.cart-item-spacer-column {
  flex-basis: 35%;
}

.cart-item-column {
  padding-right: $gutter-mobile;
}

.cart-item-image-column {
  flex-basis: 15%;

  @include breakpoint(s) {
    flex-basis: 40%;
    flex-shrink: 0;
  }

  @include breakpoint(xs) {
    margin-bottom: $gutter-mobile;
    padding-right: 0;
    flex-basis: 100%;
  }
}

.cart-item-details-column {
  display: flex;
  flex-basis: 85%;

  @include breakpoint(s) {
    display: block;

    .cart-item-gift-wrapping,
    .cart-item-quantity-column,
    .cart-item-total-column {
      margin-bottom: $gutter-mobile;
    }
  }
}

.cart-item-price-column {
  flex-basis: 15%;

  @include breakpoint(ms) {
    display: none;
  }
}

.cart-item-price-column,
.cart-item-total-column {
  flex-direction: column;
  justify-content: center;

  @include breakpoint(ms) {
    justify-content: flex-start;
    flex-direction: row;
  }
}

.cart-item-quantity-column {
  display: flex;
  flex-basis: 20%;
  align-items: center;

  .form-field-title {
    display: none;
  }

  .form-field-quantity-control {
    margin-bottom: 0;
  }

  @include breakpoint(tablet) {
    align-items: flex-start;
    flex-direction: column;

    .form-field-quantity-control {
      margin-bottom: $gutter-mobile;
    }
  }
}

.cart-item-total-column {
  flex-basis: 15%;

  @include breakpoint(tablet) {
    flex-basis: 30%;
    justify-content: flex-end;
    order: 4;
  }
}

.cart-item-remove-column {
  flex-basis: 15%;
  padding-right: 0;

  @include breakpoint(tablet-up) {
    flex-direction: row-reverse;
  }

  @include breakpoint(tablet) {
    display: none;
  }
}

.cart-item {
  display: flex;
  padding-top: rem(45);
  padding-bottom: rem(45);
  border-bottom: 1px solid $border-color;

  @include breakpoint(xs) {
    flex-direction: column;
  }
}

.cart-item-image {
  display: block;
  flex-shrink: 0;
  width: rem(90);
  height: rem(100);
  margin-right: $gutter-mobile;
  background-position: center;
  background-size: cover;
  border-bottom: none;

  @include breakpoint(s) {
    width: 100%;
    height: 100%;
  }

  @include breakpoint(xs) {
    height: 100vw;
  }
}

.cart-item-details {
  display: flex;
  flex-basis: 35%;
  flex-direction: column;
  justify-content: center;

  @include breakpoint(ms) {
    flex-basis: 50%;
  }

  dt {
    color: $nav-color;
  }

  .cart-item-gift-wrapping,
  .cart-item-sku,
  .cart-item-options,
  dt,
  dd {
    margin: 0;
    font-size: rem(13);
  }
}

.cart-item-brand {
  font-size: rem(16);
  letter-spacing: em(1, 16);
  color: $nav-color;
}

.cart-item-title {
  @extend %heading-text-treatment;
  font-size: rem(16);

  a {
    font-size: rem(16);
    letter-spacing: normal;
    text-transform: none;
    color: $heading-color;
    border-bottom: none;
  }
}

.cart-remove-link-tablet {
  display: none;

  @include breakpoint(tablet) {
    display: block;
  }
}

.cart-item-price-discounted {
  text-decoration: line-through;

  @include breakpoint(ms) {
    margin-right: rem(5);
  }
}

.cart-item-discount {
  color: $accent-color;
}

/*
 * Totals
 */

.cart-totals {
  padding-top: $local-gutter;
  padding-bottom: $local-gutter;

  @include breakpoint(tablet-up) {
    float: right;
    max-width: rem(500);
  }
}

.cart-subtotals {
  padding-bottom: rem(10);
  border-bottom: 1px solid $border-color;
}

.cart-subtotal-line {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  margin-bottom: rem(20);
}

.cart-subtotal-label {
  margin-right: rem(20);
  font-size: rem(14);
}

.cart-subtotal-content {
  font-size: rem(23);
  font-weight: 700;
  color: $heading-color;
}

.cart-coupon-codes {
  padding-top: rem(10);
}

.form-gift-certificate,
.form-coupon-code {
  position: relative;
  margin-top: rem(20);
  margin-bottom: rem(20);

  @include breakpoint(s-up) {
    display: flex;
  }
}

.button-coupon-code,
.button-gift-certificate {
  @include breakpoint(s-up) {
    height: rem(45);
    margin-left: rem(20);

    .cart-button-arrow {
      display: none;
    }
  }

  @include breakpoint(ms) {
    position: absolute;
    top: 0;
    right: 0;
    height: rem(45);
    width: rem(45);
    color: $background-color;
    background-color: $accent-color;

    .form-input:active ~ &,
    .form-input:focus ~ & {
      background-color: $nav-color;
      border-color: $nav-color;
    }

    .cart-button-text {
      display: none;
    }

    .cart-button-arrow {
      width: rem(16);
      height: rem(10);
      fill: currentColor;
    }
  }
}

.cart-grand-total {
  margin: $gutter auto;
  text-align: center;

  @include breakpoint(s-up) {
    float: left;
  }
}

.cart-total-label {
  margin-right: $gutter-mobile;
}

.cart-actions-row {
  text-align: center;

  @include breakpoint(s-up) {
    margin-top: $gutter-mobile;
    float: right;
  }
}

.additional-checkout-buttons {
  clear: both;
  text-align: center;
  float: right;
  width: 200px;

  @include breakpoint(s) {
    float: none;
    margin: $gutter auto 0;
  }
}

/*
 * Gift Wrapping
 */

.giftwrap-modal {
  .modal-content {
    max-width: rem(600);
  }

  .section-title {
    padding-bottom: $gutter;
    border-bottom: 1px solid $border-color;
  }

  .form-field-title {
    margin-right: $gutter-mobile;
  }

  .form-select-wrapper {
    display: inline-block;
  }

  .form-field-select {
    padding-top: $gutter-mobile;
    padding-bottom: $gutter-mobile;
  }

  textarea {
    height: auto;
  }

  .form-actions {
    margin-top: $gutter;
  }
}

/* scss-combine:pages/sitemap */
.sitemap-container {
  display: flex;
  flex-grow: 1;

  @include breakpoint(s) {
    flex-direction: column;
  }

  .sitemap-group {
    flex-direction: column;
    width: 33.33%;
    margin: 0 $gutter-half $gutter-half 0;

    &:last-of-type {
      margin-right: 0;
    }

    @include breakpoint(s) {
      width: 100%;
    }
  }
}

/* scss-combine:pages/blog */
.blog-container {
  margin: 0 auto;
  padding: 0 rem(20) $gutter-double;

  @include breakpoint(l-up) {
    max-width: rem(1200);
  }

  @include breakpoint(m) {
    padding-bottom: $gutter;
  }
}

.blog-content {
  margin: 0 auto;

  @include breakpoint(l-up) {
    max-width: rem(800);
  }
}

.blog-post {
  margin: rem(75) 0;

  &:first-child {
    margin-top: 0
  }

  .blog-post-date {
    text-transform: uppercase;
    letter-spacing: 1px;
    color: $nav-color;
  }

  .blog-post-title {
    @extend %heading-text-treatment;
    margin: rem(15) 0 rem(25);

    a {
      font-size: rem(26);
      text-transform: none;
      letter-spacing: 0;
      color: $heading-color;
      border-bottom: 0;
    }
  }

  .blog-post-author {
    margin-top: $gutter-half / 2;
    font-size: rem(16);
    letter-spacing: 1px;
    color: $nav-color;
  }

  .blog-post-thumbnail {
    border-bottom: 0;
  }

  .blog-post-content {
    margin: rem(25) 0 rem(15);
  }
}

.blog-divider {
  width: 100%;
  height: rem(1);
  margin: 0;

  &:first-child {
    display: none;
  }
}

/* scss-combine:pages/blog-post */
.blog-post-single {
  margin: 0 auto;

  @include breakpoint(l-up) {
    max-width: rem(1200);
  }

  .breadcrumbs {
    margin-bottom: $gutter;
  }
}

.blog-post-single-inner {
  margin: $gutter-double auto 0;

  @include breakpoint(s-up) {
    max-width: rem(800);
  }

  @include breakpoint(s) {
    margin-top: $gutter;
  }

  .blog-post-date {
    display: block;
    text-align: center;
    text-transform: uppercase;
    letter-spacing: 1px;
    color: $nav-color;
  }

  .blog-post-author {
    margin-top: ($gutter-half / 2);
    text-align: center;
    font-size: rem(16);
    letter-spacing: 1px;
    color: $nav-color;
  }

  .blog-post-title {
    margin: $gutter-half 0 rem(50);
    text-align: center;
  }

  .blog-post-tags {
    margin: ($gutter-half / 2) 0 $gutter;
    text-align: center;
  }

  .blog-post-content {
    margin-bottom: $gutter-double;

    @include breakpoint(s) {
      margin-bottom: $gutter;
    }
  }
}

.blog-post-share {
  margin-bottom: $gutter-double;

  @include breakpoint(m) {
    margin-bottom: $gutter;
  }

  @include breakpoint(s) {
    margin-bottom: $gutter-half;
  }
}

/* scss-combine:pages/compare */
.compare-page {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;

  &:after {
    flex: auto;
    content: '';
  }

  &.two-items {
    .compare-item {
      width: 50%;

      @include breakpoint(xs) {
        width: 100%;
        padding: 0;
      }
    }
  }

  &.three-items {
    .compare-item {
      width: 33.33%;

      @include breakpoint(s) {
        width: 100%;
        padding: 0;
      }
    }
  }

  @include breakpoint(s) {
    margin-top: $gutter-double;
  }
}

.compare-item {
  width: 25%;
  padding: 0 rem(10);
  margin-bottom: 0;
  word-wrap: break-word;

  &:first-of-type {
    padding-left: 0;
  }

  &:last-of-type {
    padding-right: 0;
  }

  @include breakpoint(s) {
    margin-bottom: $gutter;
    width: 50%;

    &:nth-of-type(2) {
      padding-right: 0;
    }

    &:nth-of-type(3) {
      padding-left: 0;
    }
  }
}

.compare-item-remove {
  display: inline-block;
  margin-top: $gutter;
}

.compare-item-footer-title {
  width: 100%;
  margin-bottom: $gutter-mobile;
  font-family: $primary-font;
  font-size: rem(16);
  letter-spacing: em(1, 16);
  text-transform: uppercase;
}

.compare-item-footer-section {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  margin-top: $gutter-double;
  padding-top: $gutter-double;
  border-top: 1px solid $border-color;

  &:after {
    flex: auto;
    content: '';
  }

  @include breakpoint(s) {
    margin-top: 0;
    padding-top: $gutter;
  }
}

/* scss-combine:pages/brands */
.brands-list {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;

  .brand-item {
    display: flex;
    justify-content: center;
    width: 24%;
    margin-bottom: $gutter;

    @include breakpoint(s) {
      width: 30%;
    }

    @include breakpoint(xs) {
      width: 48%;
    }
  }

  .brand-link {
    @extend %heading-text-treatment;
    font-size: rem(20);
    letter-spacing: normal;
    text-align: center;
    text-transform: none;
    border: none;

    &:hover {
      color: $accent-color;
    }
  }
}
/* scss-combine:pages/gift-certificate */
.giftcard-nav {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  padding: 0;

  @include breakpoint(s) {
    display: block;
    margin-bottom: $gutter;
  }

  .giftcard-nav-item {
    color: $accent-color;

    @include breakpoint(s) {
      margin: rem(10);
    }
  }
}

.giftcard-form {
  width: 100%;

  .form-field {
    margin-bottom: $gutter;
  }
}

.giftcard-form-actions {
  text-align: center;

  .button-primary {
    margin-bottom: $gutter-half;
    width: auto;
  }
}

/* scss-combine:pages/unavailable */
.page-hibernation,
.page-maintenance {
  padding-top: $gutter-double;
}

.hibernation-message,
.maintenance-message {
  text-align: center;
}
