components/product/tabs/description:
{{#if product.description}}
  <section
    class="product-tabs-description product-tabs-section rte"
    id="description"
    data-tab-content
  >
    {{{product.description}}}
  </section>
{{/if}}

components/product/tabs/warranty:
{{#if product.warranty}}
  <section
    class="product-tabs-section product-tabs-warranty"
    id="warranty"
    data-tab-content
  >
    <h1 class="product-warranty-title">{{lang 'product.tabs.warranty'}}</h1>
    {{{product.warranty}}}
  </section>
{{/if}}

core/forms/common/multiline:
{{!
  Textarea form component

  @param validation_message string
         Custom validation message
}}
<div class="form-field form-field-common form-field-multiline {{#if required}}form-required{{/if}}" id="{{id}}">
  <label class="form-label">
    <span class="form-field-title">
      {{label}}
      {{#if required}}<span class="required-text">{{lang 'common.required'}}</span>{{/if}}
    </span>
    <span class="form-field-control">
      <textarea
        class="form-input"
        id="textarea-{{id}}"
        name="{{name}}"
        placeholder="{{prefill}}"
        rows="{{rows}}"
        {{#if required}}
          required
          {{#if validation_message}}
            data-vd-message-required="{{validation_message}}"
          {{/if}}
        {{/if}}
        aria-required="{{required}}">{{value}}</textarea>
    </span>
  </label>
</div>
