pages/order-confirmation:
{{#partial "head"}}
    {{{ checkout.checkout_head }}}
    {{{ stylesheet '/assets/css/optimized-checkout.css' }}}
    {{ getFontsCollection }}
    <script type="text/javascript">
        window.language = {{{langJson 'optimized_checkout'}}};
    </script>
{{/partial}}
{{#partial "page"}}
    <div class="main full">
        <aside class="page-sidebar">
            {{> components/common/sidebar}}
        </aside>
        <div class="main-content">
            <header class="checkoutHeader optimizedCheckout-header">
                <div class="checkoutHeader-content">
                    <h1 class="is-srOnly">{{lang 'checkout.title'}}</h1>
                    <h2 class="checkoutHeader-heading">
                        <a class="checkoutHeader-link" href="{{urls.home}}">
                            {{#if checkout.header_image}}
                                <img alt="{{settings.store_logo.title}}" class="checkoutHeader-logo" id="logoImage" src="{{ checkout.header_image }}"/>
                            {{ else }}
                                <span class="header-logo-text">{{settings.store_logo.title}}</span>
                            {{/if}}
                        </a>
                    </h2>
                </div>
            </header>
            {{{ checkout.order_confirmation_content }}}
        </div>
    </div>
{{/partial}}
{{> layout/empty}}

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root" href="{{url}}" data-collapsible="navPages-{{id}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
    </a>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}
