components/nav-categories:
{{#if categories.length}}
  <nav class="nav-category" data-nav>
    <ul class="nav-menu">
      {{#each categories}}
        <li class="nav-menu-item"
            id="nav-menu-category-{{id}}"
            data-menu-item
            {{#each ../breadcrumbs}}
              {{#if ../url '===' url}}
                data-menu-item-breadcrumb
              {{/if}}
            {{/each}}>
          <div class="nav-menu-item-top-row" data-nav-menu-item-row>
            <a href="{{url}}" class="nav-menu-item-link">
              {{name}}
            </a>

            {{#if children}}
                <button class="submenu-toggle" data-submenu-toggle
                {{#each ../../breadcrumbs}}
                  {{#if ../name '===' name}}
                    data-submenu-toggle-breadcrumb=true
                  {{/if}}
                {{/each}}
                >
                  <svg class="submenu-toggle-icon" title="Display submenu" role="img">
                    <use xlink:href="#icon-lazy-arrow"></use>
                  </svg>
                </button>
              </div>
              <ul class="nav-submenu" data-submenu>
                {{#each children}}
                  {{> components/nav-category-item breadcrumbs=../../../breadcrumbs}}
                {{/each}}
              </ul>
            {{else}}
              </div>
            {{/if}}
        </li>
      {{/each}}
    </ul>
  </nav>
{{/if}}

components/nav-pages:
{{#if theme_settings.show_categories}}
  {{#if theme_settings.category_display '===' 'in_a_dropdown'}}
    {{> components/nav-categories-shop}}
  {{/if}}
{{/if}}
<nav class="nav-pages">
  <ul class="nav-menu">
    {{#if theme_settings.show_home_link}}
      <li class="nav-menu-item">
        <a href="{{urls.home}}" class="nav-menu-item-link">
          {{lang 'nav.home_link'}}
        </a>
      </li>
    {{/if}}
    {{#unless only_shop_link}}
      {{#each pages}}
        <li class="nav-menu-item" data-menu-item>
          <div class="nav-menu-item-top-row" data-nav-menu-item-row>
            <a href="{{url}}" class="nav-menu-item-link">
              {{name}}
            </a>

            {{#if children}}
                <button
                  class="submenu-toggle"
                  data-submenu-toggle
                  aria-label="Toggle Submenu"

                >
                  <svg class="submenu-toggle-icon" title="Display submenu" role="img">
                    <use xlink:href="#icon-lazy-arrow"></use>
                  </svg>
                </button>
              </div>
              <ul class="nav-submenu" data-submenu>
                {{#each children}}
                  {{> components/nav-category-item}}
                {{/each}}
              </ul>
            {{else}}
              </div>
            {{/if}}
        </li>
      {{/each}}
    {{/unless}}
  </ul>
</nav>

components/header/currency-selector:
{{#if currency_selector.currencies.length ">" 1}}
  <div class="form-select-wrapper currency-selector-wrapper" data-currency-selector>
    <span class="form-selected-text" data-currency-selector-toggle>{{currency_selector.active_currency_name}}</span>
    <select>
      {{#each currency_selector.currencies}}
        <option value="{{{switch_url}}}" {{#if is_active}}selected{{/if}} aria-label="{{name}}">
          {{name}}
        </option>
      {{/each}}
    </select>
  </div>
{{/if}}

components/faceted-search/index:
{{!
  @param sort (string)
         The currently active sorting method.

  @param clear_url (string)
         A URL for the current page, without any filters applied.

  @param subcategories (array)
         A list of child categories.

  @param facets_enabled (bool)
         True if the faceted search feature is enabled, false otherwise.

  @param facets (array)
         The enabed facets.

  @param facets_selected (array)
         The currently selected facet filters.

  @param shop_by_price (array)
         A list of price ranges to filter by.

  @param shop_by_brand (array)
         A list of brands to filter by.
}}
<div class="sidebar-filters-container" data-filters-container>

  <div class="filters" data-filters>
    {{! Selected Facets }}

    {{#if subcategories}}
      {{> components/faceted-search/subcategories
        categories=subcategories
      }}
    {{/if}}

    {{! Faceted Search }}

    {{#if facets_enabled}}
      {{#each facets}}
        {{#if visible}}
          {{#if items.length}}
            <div class="filter filter-{{type}}">
              <h2 class="sidebar-filter-title filter-title">{{title}}</h2>
              {{{dynamicComponent 'components/faceted-search/facets'}}}
            </div>
          {{/if}}

          {{#if facet '===' 'price'}}
            <div class="filter filter-{{type}}">
              <h2 class="sidebar-filter-title filter-title">{{title}}</h2>
              {{{dynamicComponent 'components/faceted-search/facets'}}}
            </div>
          {{/if}}
        {{/if}}
      {{/each}}

    {{! Regular Filters }}
    {{else}}
      {{#if shop_by_brand.length}}
        {{> components/faceted-search/shop-by-brand
          shop_by_brand=shop_by_brand
        }}
      {{/if}}

      {{#if shop_by_price.length}}
        {{> components/faceted-search/shop-by-price
          shop_by_price=shop_by_price
          clear_url=clear_url
        }}
      {{/if}}
    {{/if}}
  </div>

</div>

components/faceted-search/subcategories:
{{!
  @param categories
         A list of categories.
}}
<div class="filter filter-subcategories">
  <h2 class="sidebar-filter-title filter-title">{{lang 'filter.subcategories.title'}}</h2>
  <ul class="filter-list filter-subcategories-list filter-subcategories-partial">
    {{#each (limit categories 5)}}
      <li class="sidebar-filter-item filter-item">
        <a class="sidebar-filter-item-link filter-item-link" href="{{url}}">{{name}}</a>
      </li>
    {{/each}}
  </ul>
  {{#if categories.length '>' 5}}
    <ul class="filter-list filter-subcategories-list filter-subcategories-full">
      {{#each categories}}
        <li class="sidebar-filter-item filter-item">
          <a class="sidebar-filter-item-link filter-item-link" href="{{url}}">{{name}}</a>
        </li>
      {{/each}}
    </ul>
    <span class="sidebar-filter-item-link-selected sidebar-filter-item-link filter-item-link"
          data-show-more-subcategories>
      <span class="toggle-link-text toggle-link-text-off">{{lang 'filter.sort.show_more'}}</span>
      <span class="toggle-link-text toggle-link-text-on">{{lang 'filter.sort.show_less'}}</span>
    </span>
  {{/if}}
</div>

components/faceted-search/shop-by-brand:
{{!
  @param shop_by_brand
         The brand filters, as supplied by Stencil.
}}
<div class="filter filter-bybrand">
  <h2 class="sidebar-filter-title filter-title">{{lang 'filter.brand.title'}}</h2>

  <ul class="filter-list">
    {{#each (limit shop_by_brand 10)}}
      <li class="sidebar-filter-item filter-item">
        <a class="sidebar-filter-item-link filter-item-link" href="{{url}}">
          {{name}}
        </a>
      </li>
    {{/each}}
    {{#if shop_by_brand.length '>' 9}}
      <li class="sidebar-filter-item filter-item">
        <a class="sidebar-filter-item-link filter-item-link" href="{{urls.brands}}">
          {{lang 'filter.brand.view_all'}}
        </a>
      </li>
    {{/if}}
  </ul>
</div>

components/faceted-search/shop-by-price:
{{!
  @param clear_url
         The URL for the page without filters.

  @param shop_by_price
         The price range filters, as supplied by Bigcommerce.
}}
<div class="filter filter-byprice">
  <h2 class="sidebar-filter-title filter-title">{{lang 'filter.price.title'}}</h2>
  <ul class="filter-list">
    {{#each shop_by_price}}
      <li class="sidebar-filter-item filter-item {{#if selected}}selected{{/if}}">
        <a class="sidebar-filter-item-link filter-item-link" href="{{url}}" data-faceted-search-facet>{{lang 'filter.price.range' low=low.formatted high=high.formatted}}</a>
      </li>
    {{/each}}

    {{#any shop_by_price selected=true}}
      <li class="sidebar-filter-item-selected sidebar-filter-item filter-item">
        <a class="sidebar-filter-item-link-selected sidebar-filter-item-link filter-item-link" href="{{../clear_url}}" data-faceted-search-facet>{{lang 'filter.clear_selection'}}</a>
      </li>
    {{/any}}
  </ul>
</div>
