pages/search:
---
product_results:
  limit: {{theme_settings.product-count}}
---

{{#partial "page"}}
  {{> components/breadcrumbs}}

  {{#any content_results product_results.products}}
    <h1 class="section-title products-section-title"> {{lang 'search.results.count' search_term=forms.search.query}}</h1>
  {{else}}
    {{#if forms.search.query}}
      <h1 class="section-title products-section-title"> {{lang 'search.results.none' search_term=forms.search.query}}</h1>
    {{else}}
      <h1 class="section-title products-section-title"> {{lang 'search.results.no_search_term'}}</h1>
    {{/if}}
  {{/any}}

  <section data-tabs>
    <ul class="search-tabs">
      <li class="search-tab-title tab-title">
        <a href="#search-product-listing" class="search-tab-link" data-tab-link>
          {{lang 'search.results.products' num=product_results.pagination.total}}
        </a>
      </li>
      <li class="search-tab-title tab-title">
        <a href="#search-content-listing" class="search-tab-link" data-tab-link>
          {{lang 'search.results.content' num=pagination.content_results.total}}
        </a>
      </li>
    </ul>
    {{> components/search/products}}
    {{> components/search/content}}
  </section>

{{/partial}}

{{> layout/base}}

components/search/selected:
{{#if product_results.selected.items.length}}
  <div class="filter-selected">
    <ul class="filter-selected-list">
      {{#each product_results.selected.items}}
        <li class="filter-selected-item" data-facet-item>
          <a class="filter-selected-item-link" href="{{url}}" data-faceted-search-facet>
            {{title}}
            <svg class="filter-selected-item-icon" title="Select filter" role="img">
              <use xlink:href="#icon-close"></use>
            </svg>
          </a>
        </li>
      {{/each}}
    </ul>

    <a class="filter-reset button" href="{{brand.selected.remove_all_url}}" data-faceted-search-facet>
      {{lang 'filter.clear_selection'}}
    </a>
  </div>
{{/if}}

components/product/product-item:
{{!
  Product listing item

  @param default_image
         The image to use if the product has no image assigned.
}}

<article
  class="product-item {{prefix}}-item {{#if out_of_stock_message}}product-item-sold-out{{/if}}"
  data-product-item
>
  {{#if out_of_stock_message}}
    {{#if prefix '!==' 'compare'}}
      <div class="product-item-tag {{prefix}}-item-tag tag-sold-out">{{out_of_stock_message}}</div>
    {{/if}}
  {{else}}
    {{#if price.saved}}
      <div class="product-item-tag {{prefix}}-item-tag tag-sale">{{lang 'core.product.sale'}}</div>
    {{/if}}
  {{/if}}

  <div class="product-item-image {{prefix}}-item-image aspect-{{settings.grid_aspect_ratio}}">
    <a href="{{url}}"
       title="{{name}}"
       style="background-image:url({{getImage image 'medium' (cdn settings.default_image)}});"
    >
      <img src="{{getImage image 'medium' (cdn settings.default_image)}}" alt="{{image.alt}}">
    </a>
  </div>

  <div class="product-item-details">
    {{#if prefix '===' 'compare'}}
      {{#if rating}}
        {{> components/reviews/ratings rating=rating}}
      {{/if}}
    {{/if}}

    {{#if show_compare}}
      <label class="product-item-compare {{prefix}}-item-compare">
        <input
          type="checkbox"
          class="form-input form-radio"
          data-compare-checkbox
          data-compare-id="{{id}}"
          data-compare-title="{{name}}"
          data-compare-url="{{url}}"
          data-compare-thumbnail="{{getImage image 'medium' (cdn settings.default_image)}}">
        <span class="form-label-text">{{lang 'core.product.compare'}}</span>
      </label>
    {{/if}}

    <h1 class="product-item-title {{prefix}}-item-title">
      <a href="{{url}}">{{name}}</a>
    </h1>

    <div class="product-item-price {{prefix}}-item-price">
      {{> components/product/price}}
    </div>

    {{#if show_cart_action}}
      {{#if has_options}}
        <a
          href="{{url}}"
          class="button button-primary button-cart-action"
          data-product-id="{{id}}"
        >
          {{lang 'product.choose_options'}}
        </a>
      {{/if}}
      {{#if pre_order}}
        <a href="{{url}}" class="button button-primary button-cart-action">{{lang 'product.pre_order'}}</a>
      {{/if}}
      {{#if add_to_cart_url}}
        <a href="{{add_to_cart_url}}" class="button button-primary button-cart-action">{{lang 'product.add_to_cart'}}</a>
      {{/if}}
    {{/if}}

    {{#if settings.quick_view}}
      <button
        class="button button-secondary button-quick-view"
        data-quick-view
        data-product-id="{{id}}"
      >
        {{lang 'product.quick_view'}}
      </button>
    {{/if}}
  </div>

  {{#if prefix '===' 'compare'}}
    <a href="{{remove_url}}" class="compare-item-remove">{{lang 'compare.remove'}}</a>
  {{/if}}
</article>

components/search/pagination:
{{#if type '===' 'products'}}
  {{> components/common/pagination pagination.product_results}}
{{/if}}

{{#if type '===' 'content'}}
  {{> components/common/pagination pagination.content_results}}
